import React from 'react';
import { connect } from 'react-redux';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';
import * as actions from '../../Redux/Actions/actions.js';
import Select from 'react-select';
import { Button } from '@mui/material';

class UpdateEmployeeModal extends React.PureComponent {
  render() {
    var profilePhoto;
    var hireDate = this.props.singleResponseData.hireDate;
    var firstName = this.props.singleResponseData.firstName;
    var lastName = this.props.singleResponseData.lastName;
    var phone = this.props.singleResponseData.phoneNumber;
    var email = this.props.singleResponseData.email;
    var qualification;
    var procedureType = '';
    this.props.responseDataList.qualification.forEach((element) => {
      if (element.value === this.props.singleResponseData.qualification)
        qualification = element.name;
    });
    var procedureType;
    var role;
    this.props.responseDataList.roles.forEach((element) => {
      if (element.value === this.props.singleResponseData.role)
        role = element.name;
    });

    if (this.props.singleResponseData !== '') {
      procedureType =
        this.props.singleResponseData.procedureType.procedureTypeName;
    }

    var employeeInfoArea;
    var actionButtons;
    var employeeToUpdate = {
      employeeId: this.props.activeId,
      firstName: this.props.singleResponseData.firstName,
      lastName: this.props.singleResponseData.lastName,
      phoneNumber: this.props.singleResponseData.phoneNumber,
      password: this.props.singleResponseData.password,
      email: this.props.singleResponseData.email,
      procedureType: this.props.singleResponseData.procedureType,
      qualification: this.props.singleResponseData.qualification,
      role: this.props.singleResponseData.role,
      hireDate: this.props.singleResponseData.hireDate,
      profile: this.props.singleResponseData.profile,
      profileId: this.props.singleResponseData.profileId,
      orders: null,
    };

    var roles = [];
    this.props.responseDataList.roles.forEach((element) => {
      roles.push({ value: element.value, label: element.name });
    });

    roles.forEach((element) => {
      if (element.value === qualification) qualification = element.value;
    });

    var procedureTypesSL = [];
    this.props.responseDataList.procedureTypesSelectList.forEach((element) => {
      procedureTypesSL.push({ value: element.value, label: element.text });
    });

    var qualifications = [];
    this.props.responseDataList.qualification.forEach((element) => {
      qualifications.push({ value: element.value, label: element.name });
    });

    var orders = [];
    this.props.responseDataList.orders.forEach((order) => {
      if (
        order.employee.employeeId === this.props.singleResponseData.employeeId
      )
        orders.push(order);
    });
    // Employee orders table

    if (orders !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>Date of service</th>
            <th>Client name</th>
            <th>Client phone number</th>
            <th>Client email</th>
            <th>Procedure name</th>
            <th>Procedure cost</th>
            <th>Employee</th>
          </tr>
        </thead>
      );

      for (let i = 0; i < orders.length; i++) {
        var date = orders[i].dateOfService.substr(0, 10);
        var time = orders[i].dateOfService.substr(11, 5);
        element = '';
        element = (
          <tr key={orders[i].orderId} id={orders[i].orderId}>
            <td>{date + ' ' + time}</td>
            <td>{orders[i].client.firstName}</td>
            <td>{orders[i].client.phoneNumber}</td>
            <td>{orders[i].client.email}</td>
            <td>{orders[i].procedure.procedureName}</td>
            <td>{orders[i].procedure.procedurePrice}</td>
            <td>
              {orders[i].employee.firstName + ' ' + orders[i].employee.lastName}
            </td>
          </tr>
        );
        elements = elements.concat(element);
      }
    }

    // End of employee orders table

    if (!this.props.isEditing) {
      actionButtons = (
        <div className="editUserInfo">
          <button className="modalButton" onClick={this.props.onEdit}>
            Edit profile
          </button>
          <button
            className="modalButton"
            onClick={() => {
              this.props.onDelete(this.props.activeId, this.props.activeMenu);
              this.props.setUpdateModalState();
            }}
          >
            Delete profile
          </button>
        </div>
      );

      employeeInfoArea = (
        <>
          <div className="profileText">
            <div className="profileTextItem">
              <p>First name: {firstName}</p>
            </div>

            <div className="profileTextItem">
              <p>Last name: {lastName}</p>
            </div>

            <div className="profileTextItem">
              <p>Hired: {hireDate}</p>
            </div>

            <div className="profileTextItem">
              <p>Phone: {phone}</p>
            </div>

            <div className="profileTextItem">
              <p>Email: {email}</p>
            </div>

            <div className="profileTextItem">
              <p>ProcedureType: {procedureType}</p>
            </div>

            <div className="profileTextItem">
              <p>Qualification: {qualification}</p>
            </div>

            <div className="profileTextItem">
              <p>Role: {role}</p>
            </div>
          </div>
        </>
      );
    } else {
      actionButtons = (
        <div className="confirmUserInfo">
          <div>
            <form>
              <input
                className="profilePhotoInput"
                type="file"
                onChange={(e) => {
                  console.log(e.target.value);
                  profilePhoto = e.target.value;
                }}
              />
              <button
                className="modalButton"
                type="submit"
                onSubmit={() => {
                  const formData = new FormData();
                  formData.append('body', profilePhoto);
                  this.props.uploadPhoto(this.props.activeId, formData);
                }}
              >
                Save photo
              </button>
            </form>
          </div>
          <div>
            <button
              className="modalButton"
              onClick={() => {
                this.props.onEdit();
                this.props.setUpdateModalState();
                this.props.onUpdate(this.props.activeMenu, employeeToUpdate);
              }}
            >
              Save changes
            </button>
          </div>
        </div>
      );

      employeeInfoArea = (
        <div className="profileText">
          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>First name:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="text"
                placeholder={firstName}
                onChange={(e) => {
                  employeeToUpdate.firstName = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Last name:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="text"
                placeholder={lastName}
                onChange={(e) => {
                  employeeToUpdate.lastName = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Hired:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="date"
                placeholder={hireDate}
                onChange={(e) => {
                  employeeToUpdate.hireDate = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Phone:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="tel"
                placeholder={phone}
                onChange={(e) => {
                  employeeToUpdate.phoneNumber = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Email:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="email"
                placeholder={email}
                onChange={(e) => {
                  employeeToUpdate.email = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Role:</p>
            </div>
            <div className="profileTextItem-right">
              <Select
                onChange={(data) => {
                  employeeToUpdate.role = data.value;
                }}
                options={roles}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>ProcedureType:</p>
            </div>
            <div className="profileTextItem-right">
              <Select
                options={procedureTypesSL}
                onChange={(data) => {
                  this.props.responseDataList.procedureTypes.forEach(
                    (element) => {
                      if (element.procedureTypeName === data.label) {
                        employeeToUpdate.procedureType = element;
                      }
                    },
                  );
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Qualification:</p>
            </div>
            <div className="profileTextItem-right">
              <Select
                options={qualifications}
                onChange={(data) => {
                  employeeToUpdate.qualification = data.value;
                }}
              />
            </div>
          </div>
        </div>
      );
    }

    return (
      <Modal
        styles={{
          modal: {
            maxWidth: 'fit-content',
            background: '#6c3636',
            padding: '40px',
            fontFamily: 'Play',
            alignItems: 'center',
            textAlign: 'center',
            border: '6px solid #f7f6f6',
            borderRadius: '10px',
          },
        }}
        open={this.props.updateModalOpen}
        center={true}
        showCloseIcon={false}
        onOverlayClick={this.props.setUpdateModalState}
      >
        <>
          <div className="profileBody">
            <div className="profilePhoto">
              <img src={this.props.employeeProfilePhoto} alt="profile" />
            </div>
            {employeeInfoArea}
          </div>
          {actionButtons}
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
            </table>
          </div>
        </>
      </Modal>
    );
  }
}

function mapStateToProps(state) {
  return {
    updateModalOpen: state.updateModalOpen,
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
    isEditing: state.isEditing,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
    employeeProfilePhoto: state.employeeProfilePhoto,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setUpdateModalState: () => dispatch(actions.SetUpdateModalState()),
    onEdit: () => dispatch(actions.SetEditState()),
    onDelete: (id, menu) => {
      dispatch(actions.DeleteData(id, menu));
    },
    onUpdate: (activeMenu, data) =>
      dispatch(actions.UpdateData(activeMenu, data)),
    uploadPhoto: (id, profilePhoto) =>
      dispatch(actions.UploadProfilePhoto(id, profilePhoto)),
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(UpdateEmployeeModal);
