import React from 'react';
import { connect } from 'react-redux';
import Select from 'react-select';

class DropdownList extends React.PureComponent {
  render() {
    var list = [];
    this.props.listToProcess.forEach((element) => {
      list.push({ value: element.value, label: element.name });
    });

    return (
      <div className="profileTextItem">
        <div className="profileTextItem-left">
          <p>{this.props.name}:</p>
        </div>
        <div className="profileTextItem-right">
          <Select
            options={list}
            onChange={(value) => {
              this.props.chosenValue = value;
            }}
          />
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
  };
}

function mapDispatchToProps(dispatch) {
  return {};
}

export default connect(mapStateToProps, mapDispatchToProps)(DropdownList);
