import React from 'react';
import { connect } from 'react-redux';
import {
  ClientsFeetchData,
  SetActiveIdentifier,
} from '../Redux/Actions/actions';

function setColor() {
  // let previousActive = document
  //   .getElementsByTagName('tr')
  //   .classList.remove('active');
  // previousActive.classList.remove('active');
  document.getElementById(`${this.props.activeId}`).classList.add('active');
}

class ClientsList extends React.PureComponent {
  render() {
    if (this.props.clientsList !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>Name</th>
            <th>Lastname</th>
            <th>Phone</th>
            <th>Email</th>
          </tr>
        </thead>
      );

      for (let i = 0; i < this.props.clientsList.length; i++) {
        element = '';
        element = (
          <tr
            className="clientsTable"
            key={this.props.clientsList[i].clientId}
            id={this.props.clientsList[i].clientId}
            onClick={() => {
              this.props.setActiveId(this.props.clientsList[i].clientId);
              setColor();
            }}
          >
            <td>{this.props.clientsList[i].firstName}</td>
            <td>{this.props.clientsList[i].lastName}</td>
            <td>{this.props.clientsList[i].phoneNumber}</td>
            <td>{this.props.clientsList[i].email}</td>
          </tr>
        );
        elements = elements.concat(element);
      }
      console.log('elements', elements);
    }

    if (this.props.clientsList !== '') {
      return (
        <>
          <div className="clientTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
            </table>
            <div className="tableButton">
              <button onClick={this.props.getClients}>ClientsList</button>
              <button>Add client</button>
            </div>
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>Push the button</p>
          <button onClick={this.props.getClients}>ClientsList</button>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    clientsList: state.clientsList,
    activeId: state.clientId,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
    getClients: () => dispatch(ClientsFeetchData()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(ClientsList);
