import {
  click,
  CLIENTS_FETC_DATA_SUCCESS,
  EMPLOYEE_AUTORIZATION_SUCCESS,
} from './Actions/actionTypes.js';

const initialState = {
  employeeToken: 'you dont need a token',
  mensHaircut: false,
  modalOpen: true,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case click:
      return {
        ...state,
        mensHaircut: true,
      };
    case CLIENTS_FETC_DATA_SUCCESS:
      return action.payload;

    case EMPLOYEE_AUTORIZATION_SUCCESS:
      console.log(action.payload);
      return {
        ...state,
        employeeToken: action.payload,
        modalOpen: false,
      };

    default:
      return state;
  }
}
