import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../../Redux/Actions/actions.js';
import CreateFeedbackModal from '../ModalWindowsComponents/CreateFeedbackModal';

class FeedbacksList extends React.PureComponent {
  render() {
    var data = this.props.pagedTablesData;
    if (data !== '') {
      var elements = [];
      var element;
      for (let i = 0; i < data.length; i++) {
        var actionButtons = '';
        var createFeedbackButton = '';
        if (this.props.employeeToken !== '') {
          if (!data[i].isVerify) {
            actionButtons = (
              <>
                <button
                  className="modalButton"
                  onClick={() => {
                    this.props.onApprove(
                      this.props.activeMenu,
                      data.feedbacks[i],
                    );
                  }}
                >
                  Approve
                </button>
                <button
                  className="modalButton"
                  onClick={() => {
                    this.props.onDelete(
                      data[i].feedbackId,
                      this.props.activeMenu,
                    );
                  }}
                >
                  Remove
                </button>
              </>
            );
          } else {
            actionButtons = (
              <>
                <button
                  className="modalButton"
                  onClick={() => {
                    this.props.onDelete(
                      data[i].feedbackId,
                      this.props.activeMenu,
                    );
                  }}
                >
                  Remove
                </button>
              </>
            );
          }
        } else {
          var createFeedbackButton = (
            <button
              className="managementMenuButton"
              id="feedback"
              onClick={(e) => {
                this.props.setCreateModalState();
              }}
            >
              Create feedback
            </button>
          );
        }

        element = '';
        element = (
          <div
            className="feedback"
            key={this.props.pagedTablesData.feedbacks[i].feedbackId}
            id={this.props.pagedTablesData.feedbacks[i].feedbackId}
          >
            <div className="feedbackTitle">
              {this.props.pagedTablesData.feedbacks[i].feedbackTitle}
            </div>
            <div className="feedbackBody">
              <div className="feedbackInfo">
                <div className="feedbackAuthor">
                  {this.props.pagedTablesData.feedbacks[i].client.firstName}
                  {''}
                  {this.props.pagedTablesData.feedbacks[i].client.lastName}
                </div>
                <div className="feedbackAuthorEmail">
                  {this.props.pagedTablesData.feedbacks[i].client.email}
                </div>
                <div className="feedbackDate">
                  {this.props.pagedTablesData.feedbacks[i].createdOn}
                </div>
                <div>{actionButtons}</div>
              </div>
              <div className="feedbackText">
                {this.props.pagedTablesData.feedbacks[i].feedbackText}
              </div>
            </div>
          </div>
        );
        elements = elements.concat(element);
      }
      console.log('elements', elements);
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          {createFeedbackButton}
          {elements}
        </>
      );
      // <CreateFeedbackModal />;
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    activeMenu: state.activeMenu,
    employeeToken: state.employeeToken,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    onApprove: (activeMenu, feedback) =>
      dispatch(actions.UpdateData(activeMenu, feedback)),
    onDelete: (id, activeMenu) => dispatch(actions.DeleteData(id, activeMenu)),
    setCreateModalState: () => dispatch(actions.SetCreateModalState()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(FeedbacksList);
