import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../../Redux/Actions/actions.js';
import { SetActiveIdentifier } from '../../Redux/Actions/actions.js';
import CreateMaterialModal from '../ModalWindowsComponents/CreateMaterialModal.js';
import UpdateMaterialModal from '../ModalWindowsComponents/UpdateMaterialModal.js';

class MaterialsList extends React.PureComponent {
  render() {
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>Name</th>
            <th>Amount</th>
            <th>Productiond date</th>
            <th>Best before date</th>
            <th>Manufacturer</th>
          </tr>
        </thead>
      );

      for (let i = 0; i < this.props.responseDataList.materials.length; i++) {
        var productionDate = this.props.responseDataList.materials[
          i
        ].productionDate.substr(0, 10);
        var bestBeforeDate = this.props.responseDataList.materials[
          i
        ].bestBeforeDate.substr(0, 10);

        var manufacturer;
        this.props.responseDataList.manufacturerSelectList.forEach(
          // eslint-disable-next-line no-loop-func
          (element) => {
            if (
              element.value ==
              this.props.responseDataList.materials[i].materialManufacturer
                .manufacturerId
            ) {
              manufacturer = element.text;
            }
          },
        );

        element = '';
        element = (
          <tr
            key={this.props.responseDataList.materials[i].materialId}
            id={this.props.responseDataList.materials[i].materialId}
            onClick={() => {
              this.props.setActiveId(
                this.props.responseDataList.materials[i].materialId,
              );
              this.props.getMaterial(
                this.props.responseDataList.materials[i].materialId,
                this.props.activeMenu,
              );
              this.props.setUpdateModalState();
            }}
          >
            <td>{this.props.responseDataList.materials[i].materialName}</td>
            <td>{this.props.responseDataList.materials[i].materialAmount}</td>
            <td>{this.props.responseDataList.materials[i].productionDate}</td>
            <td>{this.props.responseDataList.materials[i].bestBeforeDate}</td>
            <td>{manufacturer}</td>
          </tr>
        );
        elements = elements.concat(element);
      }
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <button
            className="modalButton"
            onClick={() => {
              this.props.setCreateModalState();
            }}
          >
            Add new material
          </button>
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
            </table>
            <CreateMaterialModal />
            <UpdateMaterialModal />
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setUpdateModalState: () => dispatch(actions.SetUpdateModalState()),
    setCreateModalState: () => dispatch(actions.SetCreateModalState()),
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
    getMaterial: (id, activeMenu) =>
      dispatch(actions.FetchSingleData(id, activeMenu)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(MaterialsList);
