import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../../Redux/Actions/actions.js';
import CreateOrderModal from '../ModalWindowsComponents/CreateOrderModal.js';
import UpdateOrderModal from '../ModalWindowsComponents/UpdateOrderModal.js';

class OrdersList extends React.PureComponent {
  render() {
    var date = this.props.responseDataList.orders[i].dateOfService.substr(
      0,
      10,
    );
    var time = this.props.responseDataList.orders[i].dateOfService.substr(
      11,
      16,
    );
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>Date of service</th>
            <th>Client name</th>
            <th>Client phone number</th>
            <th>Client email</th>
            <th>Procedure name</th>
            <th>Procedure cost</th>
            <th>Employee</th>
            {/* <th>Employee last name</th> */}
          </tr>
        </thead>
      );

      for (let i = 0; i < this.props.responseDataList.orders.length; i++) {
        element = '';
        element = (
          <tr
            key={this.props.responseDataList.orders[i].orderId}
            id={this.props.responseDataList.orders[i].orderId}
            onClick={() => {
              this.props.setActiveId(this.props.responseDataList[i].orderId);
              this.props.getOrder(
                this.props.responseDataList.orders[i].orderId,
                this.props.activeMenu,
              );
              this.props.setUpdateModalState();
            }}
          >
            <td>
              {this.props.responseDataList.orders[i].dateOfService.substr(
                0,
                10,
              ) +
                this.props.responseDataList.orders[i].dateOfService.substr(
                  11,
                  15,
                )}
            </td>
            <td>{this.props.responseDataList.orders[i].client.firstName}</td>
            <td>{this.props.responseDataList.orders[i].client.phoneNumber}</td>
            <td>{this.props.responseDataList.orders[i].client.email}</td>
            <td>
              {this.props.responseDataList.orders[i].procedure.procedureName}
            </td>
            <td>
              {this.props.responseDataList.orders[i].procedure.procedurePrice}
            </td>
            <td>
              {this.props.responseDataList.orders[i].employee.firstName +
                ' ' +
                this.props.responseDataList.orders[i].employee.lastName}
            </td>
          </tr>
        );
        elements = elements.concat(element);
      }
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <button
            className="modalButton"
            onClick={() => {
              this.props.setCreateModalState();
            }}
          >
            New order
          </button>
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
              <CreateOrderModal />
            </table>
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setUpdateModalState: () => dispatch(actions.SetUpdateModalState()),
    setCreateModalState: () => dispatch(actions.SetCreateModalState()),
    setActiveId: (id) => dispatch(actions.SetActiveIdentifier(id)),
    getEmployee: (id, activeMenu) =>
      dispatch(actions.FetchSingleData(id, activeMenu)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(OrdersList);
