import React from 'react';
import { connect } from 'react-redux';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';
import {
  SetUpdateModalState,
  SetEditState,
  DeleteData,
  UpdateData,
} from '../../Redux/Actions/actions.js';

class UpdateMaterialModal extends React.PureComponent {
  render() {
    var materialName = this.props.singleResponseData.materialName;
    var materialAmount = this.props.singleResponseData.materialAmount;
    var productionDate = this.props.singleResponseData.productionDate;
    var bestBeforeDate = this.props.singleResponseData.bestBeforeDate;
    var materialInfoArea;
    var actionButtons;
    var materialToUpdate = {
      materialId: this.props.activeId,
      materialName: this.props.singleResponseData.materialName,
      materialAmount: this.props.singleResponseData.materialAmount,
      productionDate: this.props.singleResponseData.productionDate,
      bestBeforeDate: this.props.singleResponseData.bestBeforeDate,
      phoneNumber: this.props.singleResponseData.phoneNumber,
      materialManufacturer: this.props.singleResponseData.materialManufacturer,
    };

    if (!this.props.isEditing) {
      actionButtons = (
        <div className="editUserInfo">
          <button className="modalButton" onClick={this.props.onEdit}>
            Edit material
          </button>
          <button
            className="modalButton"
            onClick={() => {
              this.props.onDelete(this.props.activeId, this.props.activeMenu);
              this.props.setUpdateModalState();
            }}
          >
            Delete material
          </button>
        </div>
      );

      materialInfoArea = (
        <div className="profileText">
          <div className="profileTextItem">
            <p>Material name: {materialName}</p>
          </div>

          <div className="profileTextItem">
            <p>Material amount: {materialAmount}</p>
          </div>

          <div className="profileTextItem">
            <p>Production date: {productionDate}</p>
          </div>

          <div className="profileTextItem">
            <p>Best before date: {bestBeforeDate}</p>
          </div>
        </div>
      );
    } else {
      actionButtons = (
        <div className="confirmUserInfo">
          <div>
            <button
              className="modalButton"
              onClick={() => {
                this.props.onEdit();
                this.props.setUpdateModalState();
                this.props.onUpdate(this.props.activeMenu, materialToUpdate);
              }}
            >
              Save changes
            </button>
          </div>
        </div>
      );

      materialInfoArea = (
        <div className="profileText">
          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Material name:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="text"
                defaultValue={materialName}
                onChange={(e) => {
                  materialToUpdate.firstName = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Material amount:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="text"
                defaultValue={materialAmount}
                onChange={(e) => {
                  materialToUpdate.lastName = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Production date:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="date"
                defaultValue={productionDate}
                onChange={(e) => {
                  materialToUpdate.phoneNumber = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Best before date:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="date"
                defaultValue={bestBeforeDate}
                onChange={(e) => {
                  materialToUpdate.email = e.target.value;
                }}
              />
            </div>
          </div>
        </div>
      );
    }

    return (
      <Modal
        styles={{
          modal: {
            background: 'wheat',
            padding: '40px',
            fontFamily: 'Play',
            alignItems: 'center',
            textAlign: 'center',
            fontSize: '45px',
            border: '3px solid black',
            borderRadius: '10px',
          },
        }}
        open={this.props.updateModalOpen}
        center={true}
        showCloseIcon={false}
        onOverlayClick={this.props.setUpdateModalState}
      >
        <>
          <div className="profileBody">{materialInfoArea}</div>
          {actionButtons}
        </>
      </Modal>
    );
  }
}

function mapStateToProps(state) {
  return {
    updateModalOpen: state.updateModalOpen,
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
    isEditing: state.isEditing,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setUpdateModalState: () => dispatch(SetUpdateModalState()),
    onEdit: () => dispatch(SetEditState()),
    onDelete: (id, menu) => {
      dispatch(DeleteData(id, menu));
    },
    onUpdate: (activeMenu, data) => dispatch(UpdateData(activeMenu, data)),
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(UpdateMaterialModal);
