export function Search(userInput) {
  var rows = document.getElementsByTagName('tr');
  for (let i = 0; i < rows.length; i++) {
    for (let cell in rows[i].cells) {
      if (cell.innerText.contains(userInput)) {
        rows[i].classList.toggle('active');
      }
    }
  }
}
