import React from 'react';
import { connect } from 'react-redux';
import { DeleteData, UpdateData } from '../../Redux/Actions/actions.js';
import CreateFeedbackModal from '../ModalWindowsComponents/CreateFeedbackModal';

class FeedbacksList extends React.PureComponent {
  render() {
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      for (let i = 0; i < this.props.responseDataList.length; i++) {
        var actionButtons = '';
        if (this.props.employeeToken !== '') {
          if (!this.props.responseDataList[i].isVerify) {
            actionButtons = (
              <>
                <button
                  className="modalButton"
                  onClick={() => {
                    this.props.onApprove(
                      this.props.activeMenu,
                      this.props.responseDataList[i],
                    );
                  }}
                >
                  Approve
                </button>
                <button
                  className="modalButton"
                  onClick={() => {
                    this.props.onDelete(
                      this.props.responseDataList[i].feedbackId,
                      this.props.activeMenu,
                    );
                  }}
                >
                  Remove
                </button>
              </>
            );
          } else {
            actionButtons = (
              <>
                <button
                  className="modalButton"
                  onClick={() => {
                    this.props.onDelete(
                      this.props.responseDataList[i].feedbackId,
                      this.props.activeMenu,
                    );
                  }}
                >
                  Remove
                </button>
              </>
            );
          }
        }

        element = '';
        element = (
          <div
            className="feedback"
            key={this.props.responseDataList[i].feedbackId}
            id={this.props.responseDataList[i].feedbackId}
          >
            <div className="feedbackTitle">
              {this.props.responseDataList[i].feedbackTitle}
            </div>
            <div className="feedbackBody">
              <div className="feedbackInfo">
                <div className="feedbackAuthor">
                  {this.props.responseDataList[i].client.firstName}
                  {''}
                  {this.props.responseDataList[i].client.lastName}
                </div>
                <div className="feedbackAuthorEmail">
                  {this.props.responseDataList[i].client.email}
                </div>
                <div className="feedbackDate">
                  {this.props.responseDataList[i].createdOn}
                </div>
                <div>{actionButtons}</div>
              </div>
              <div className="feedbackText">
                {this.props.responseDataList[i].feedbackText}
              </div>
            </div>
          </div>
        );
        elements = elements.concat(element);
      }
      console.log('elements', elements);
    }

    if (this.props.responseDataList !== '') {
      return <>{elements}</>;
      <CreateFeedbackModal />;
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    onApprove: (activeMenu, feedback) =>
      dispatch(UpdateData(activeMenu, feedback)),
    onDelete: (id, activeMenu) => dispatch(DeleteData(id, activeMenu)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(FeedbacksList);
