import React from 'react';
import ReactDOM from 'react-dom';
import { connect } from 'react-redux';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';
import { SetModalState } from '../../Redux/Actions/actions.js';
import testImage from '../../temporaryFiles/example3.jpg';

class EmployeeModal extends React.PureComponent {
  render() {
    var date = this.props.singleResponseData.hireDate;
    // date = date.substring(0, 10);
    var name = `${this.props.singleResponseData.firstName} ${this.props.singleResponseData.lastName}`;
    var phone = this.props.singleResponseData.phoneNumber;
    var email = this.props.singleResponseData.email;
    var modal;
    if (!this.props.isEditing) {
      modal = (
        <Modal
          styles={{
            modal: {
              background: 'wheat',
              padding: '40px',
              fontFamily: 'Play',
              alignItems: 'center',
              textAlign: 'center',
              fontSize: '45px',
              border: '3px solid black',
              borderRadius: '10px',
            },
          }}
          open={this.props.open}
          center={true}
          showCloseIcon={false}
          onOverlayClick={this.props.setModalState}
        >
          <>
            <div className="profileBody">
              <div className="profilePhoto">
                <img src={testImage} alt="profile" />
              </div>
              <div className="profileText">
                <p>Name: {name}</p>
                <p>Hired: {date}</p>
                <p>Phone: {phone}</p>
                <p>Email: {email}</p>
                <p>specialization</p>
              </div>
            </div>

            <div>
              <button className="modalButton">Edit profile</button>
              <button className="modalButton">Delete profile</button>
            </div>
          </>
        </Modal>
      );
    }

    return (
      <Modal
        styles={{
          modal: {
            background: 'wheat',
            padding: '40px',
            fontFamily: 'Play',
            alignItems: 'center',
            textAlign: 'center',
            fontSize: '45px',
            border: '3px solid black',
            borderRadius: '10px',
          },
        }}
        open={this.props.open}
        center={true}
        showCloseIcon={false}
        onOverlayClick={this.props.setModalState}
      >
        <>
          <div className="profileBody">
            <div className="profilePhoto">
              <img src={testImage} alt="profile" />
            </div>
            <div className="profileText">
              <p>Name: {name}</p>
              <p>Hired: {date}</p>
              <p>Phone: {phone}</p>
              <p>Email: {email}</p>
              <p>specialization</p>
            </div>
          </div>

          <div>
            <button className="modalButton">Edit profile</button>
            <button className="modalButton">Delete profile</button>
          </div>
        </>
      </Modal>
    );
  }
}

function mapStateToProps(state) {
  return {
    open: state.open,
    singleResponseData: state.singleResponseData,
    isEditing: state.isEditing,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setModalState: () => dispatch(SetModalState()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(EmployeeModal);
