export default function StyleChange(id, className) {
  var elements;
  var active;
  var activeButton;
  if (className) {
    elements = document.getElementsByClassName(className);

    for (let i = 0; i < elements.length; i++) {
      if (elements[i].getAttribute('className').contains('active')) {
        elements[i].toggle('active');
      }
    }
    // active = document.querySelector('.active');

    // if (active !== null) {
    //   active.classList.toggle('active');
    // }
    // activeButton = elements.getElementById(`${id}`);
    // activeButton.classList.toggle('active');
  } else {
    active = document.querySelector('.active');
    if (active !== null) {
      active.classList.toggle('active');
    }
    activeButton = document.getElementById(`${id}`);
    activeButton.classList.toggle('active');
  }
}
