import * as TYPES from './actionTypes';
const host = 'https://localhost:7275';

// Authorization of employees ==========================================

export function EmployeeAutorization(email, password) {
  const requestURL = `${host}/Employee/GetToken?employeeEmail=${email}&password=${password}`;
  return (dispatch) => {
    fetch(requestURL, {
      method: 'post',
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response;
      })
      .then(async (response) => {
        const data = await response.json();
        dispatch(EmployeeAutorizationSuccess(data));
      });
  };
}

export function EmployeeAutorizationSuccess(data) {
  return {
    type: TYPES.EMPLOYEE_AUTORIZATION_SUCCESS,
    payload: data,
  };
}

export function EmployeeLogout() {
  return {
    type: TYPES.EMPLOYEE_LOGOUT,
  };
}
//==============================================
//==============================================
//Get paged data of current table
export function FeetchMainTableData(activeMenu, itemsPerPage = 5) {
  var requestURL;
  switch (activeMenu) {
    case 'clientsPage':
      requestURL = `${host}/Client/GetAllClients`;
      break;
    case 'materialsPage':
      requestURL = `${host}/Material/GetAllMaterials?elementsPerPage=${itemsPerPage}`;
      break;
    case 'servicesPage':
      requestURL = `${host}/Procedure/GetAllProcedures`;
      break;
    case 'ordersPage':
      requestURL = `${host}/Order/GetAllOrders`;
      break;
    case 'promoPage':
      requestURL = `${host}/Profile/GetAllProfiles`;
      break;
    case 'feedbacksPage':
      requestURL = `${host}/Feedback/GetAllFeedbacks`;
      break;
    case 'staffPage':
      requestURL = `${host}/Employee/GetAllEmployees`;
      break;
    case 'mediaFilesPage':
      requestURL = `${host}/MediaFile/GetAllMediaFiles`;
      break;
    default:
      break;
  }
  return (dispatch) => {
    fetch(requestURL)
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response;
      })
      .then(async (response) => {
        const data = await response.json();
        dispatch(FeetchMainTableDataSuccess(data, activeMenu));
      });
  };
}

export function FeetchMainTableDataSuccess(data, id) {
  return {
    type: TYPES.FETCH_TABLE_DATA_SUCCESS,
    payload: data,
    identifier: id,
  };
}
//========================================================
//========================================================
//Get the data of choosen record of the table by ID
export function FetchActiveRecordData(id, activeMenu) {
  var requestURL;
  switch (activeMenu) {
    case 'clientsPage':
      requestURL = `${host}/Client/GetClientById?id=${id}`;
      break;
    case 'materialsPage':
      requestURL = `${host}/Material/GetMaterialById?id=${id}`;
      break;
    case 'servicesPage':
      requestURL = `${host}/Procedure/GetServiceById?id=${id}`;
      break;
    case 'ordersPage':
      requestURL = `${host}/Order/GetOrderById?id=${id}`;
      break;
    case 'promoPage':
      requestURL = `${host}/Profile/GetProfileById?id=${id}`;
      break;
    case 'feedbacksPage':
      requestURL = `${host}/Feedback/GetFeedbackById?id=${id}`;
      break;
    case 'staffPage':
      requestURL = `${host}/Employee/GetEmployeeById?id=${id}`;
      break;
    case 'mediaFilesPage':
      requestURL = `${host}/MediaFile/GetFileById?id=${id}`;
      break;
    default:
      break;
  }
  return (dispatch) => {
    fetch(requestURL)
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response;
      })
      .then(async (response) => {
        const data = await response.json();
        dispatch(FetchActiveRecordDataSuccess(data));
      });
  };
}

export function FetchClientByEmail(email) {
  var requestURL = `${host}/Client/GetClientByEmail?email=${email}`;
  return (dispatch) => {
    fetch(requestURL)
      .then((response) => {
        if (!response.ok) {
          dispatch(FetchActiveRecordData('Not found'));
        }
        console.log(response);
        return response;
      })
      .then(async (response) => {
        const data = await response.json();
        dispatch(FetchActiveRecordDataSuccess(data));
      });
  };
}

export function FetchActiveRecordDataSuccess(data) {
  return {
    type: TYPES.SINGLE_RECORD_FETCH_DATA_SUCCESS,
    payload: data,
  };
}
//========================================================
//========================================================
//Delete the data of choosen record of the table

export function DeleteData(id, activeMenu) {
  var requestURL;
  switch (activeMenu) {
    case 'clientsPage':
      requestURL = `${host}/Client/DeleteClientById?id=${id}`;
      break;
    case 'materialsPage':
      requestURL = `${host}/Material/DeleteMaterialById?id=${id}`;
      break;
    case 'servicesPage':
      requestURL = `${host}/Procedure/DeleteServiceById?id=${id}`;
      break;
    case 'ordersPage':
      requestURL = `${host}/Order/DeleteOrderById?id=${id}`;
      break;
    case 'profilesPage':
      requestURL = `${host}/Profile/DeleteProfileById?id=${id}`;
      break;
    case 'promoPage':
      requestURL = `${host}/Profile/DeleteProfileById?id=${id}`;
      break;
    case 'feedbacksPage':
      requestURL = `${host}/Feedback/DeleteFeedbackById?id=${id}`;
      break;
    case 'staffPage':
      requestURL = `${host}/Employee/DeleteEmployeeById?id=${id}`;
      break;
    default:
      break;
  }
  return (dispatch) => {
    fetch(requestURL, { method: 'post', cors: 'no-cors' }).then(() =>
      dispatch(FeetchMainTableData(activeMenu)),
    );
  };
}

//========================================================
//========================================================
// Update the data of choosen record of the table

export function UpdateData(activeMenu, dataObject) {
