import React from 'react';
import { connect } from 'react-redux';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';
import {
  SetCreateModalState,
  AddData,
  GetProceduresByType,
  GetEmployeesByProcedureType,
  GetAvaliableTimes,
} from '../../Redux/Actions/actions.js';
import Select from 'react-select';

class CreateOrderModal extends React.PureComponent {
  render() {
    var orderInfoArea;
    var actionButtons;
    var orderToCreate = {
      dateOfService: '',
      client: '',
      procedure: '',
      employee: '',
    };

    var clientsList = [];
    this.props.responseDataList.clients.forEach((element) => {
      clientsList.push({
        value: element.clientId,
        label: element.firstName + ' ' + element.lastName,
      });
    });

    var procedureTypesList = [];
    this.props.responseDataList.procedureTypes.forEach((element) => {
      procedureTypesList.push({
        value: element.value,
        label: element.text,
      });
    });

    var procedureList = [];
    if (this.props.procedures !== '') {
      this.props.procedures.forEach((element) => {
        procedureList.push({
          value: element.procedureId,
          label: element.procedureName,
        });
      });
    }

    var employeeList = [];
    if (this.props.employees !== '') {
      this.props.employees.forEach((element) => {
        employeeList.push({
          value: element.employeeId,
          label: element.firstName + ' ' + element.lastName,
        });
      });
    }

    var scheduleTimesList = [];

    actionButtons = (
      <div className="confirmUserInfo">
        <div>
          <button
            className="modalButton"
            onClick={() => {
              this.props.setCreateModalState();
              this.props.onCreate(this.props.activeMenu, orderToCreate);
            }}
          >
            Create
          </button>
        </div>
      </div>
    );

    orderInfoArea = (
      <div className="profileText">
        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Date of service:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="date"
              placeholder="Date..."
              onChange={(e) => {
                orderToCreate.dateOfService = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>ClientName:</p>
          </div>
          <div className="profileTextItem-right">
            <Select
              maxMenuHeight={100}
              options={clientsList}
              onChange={(data) => {
                this.props.responseDataList.clients.forEach((client) => {
                  if (client.clientId == data.value)
                    orderToCreate.client = client;
                });
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Procedure type:</p>
          </div>
          <div className="profileTextItem-right">
            <Select
              maxMenuHeight={100}
              options={procedureTypesList}
              onChange={(data) => {
                this.props.getProcedures(data.value);
                this.props.getEmployees(data.value);
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Procedure:</p>
          </div>
          <div className="profileTextItem-right">
            <Select
              isDisabled={this.props.procedureInputStatus}
              maxMenuHeight={100}
              onChange={(data) => {
                this.props.procedures.forEach((procedure) => {
                  if (procedure.procedureId == data.value)
                    orderToCreate.procedure = procedure;
                });
              }}
              options={procedureList}
            />
          </div>
        </div>
        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Employee:</p>
          </div>
          <div className="profileTextItem-right">
            <Select
              isDisabled={this.props.procedureInputStatus}
              maxMenuHeight={100}
              onChange={(data) => {
                this.props.employees.forEach((employee) => {
                  if (employee.employeeId == data.value)
                    orderToCreate.employee = employee;
                });
                this.props.getSchedule(
                  orderToCreate.employee.employeeId,
                  orderToCreate.dateOfService,
                  orderToCreate.procedure.procedureId,
                  this.props.openTime,
                  this.props.closeTime,
                );
              }}
              options={employeeList}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Time of service:</p>
          </div>
          <div className="profileTextItem-right">
            <Select
              isDisabled={this.props.procedureInputStatus}
              maxMenuHeight={100}
              onChange={(data) => {
                orderToCreate.dateOfService = data;
              }}
              // options={timeList}
            />
          </div>
        </div>
      </div>
    );

    return (
      <Modal
        styles={{
          modal: {
            background: 'wheat',
            padding: '40px',
            fontFamily: 'Play',
            alignItems: 'center',
            textAlign: 'center',
            fontSize: '45px',
            border: '3px solid black',
            borderRadius: '10px',
          },
        }}
        open={this.props.createModalOpen}
        center={true}
        showCloseIcon={false}
        onOverlayClick={this.props.setCreateModalState}
      >
        <>
          <div className="profileBody">{orderInfoArea}</div>
          {actionButtons}
        </>
      </Modal>
    );
  }
}

function mapStateToProps(state) {
  return {
    openTime: state.openTime,
    closeTime: state.closeTime,
    createModalOpen: state.createModalOpen,
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
    procedures: state.procedures,
    employees: state.employees,
    procedureInputStatus: state.procedureInputStatus,
    schedule: state.schedule,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setCreateModalState: () => dispatch(SetCreateModalState()),
    onCreate: (activeMenu, data) => dispatch(AddData(activeMenu, data)),
    getProcedures: (typeId) => dispatch(GetProceduresByType(typeId)),
    getEmployees: (typeId) => dispatch(GetEmployeesByProcedureType(typeId)),
    getSchedule: (employeeId, dateOfService, procedureId, open, close) =>
      dispatch(
        GetAvaliableTimes(employeeId, dateOfService, procedureId, open, close),
      ),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateOrderModal);
