import React from 'react';
import { connect } from 'react-redux';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';
import { SetCreateModalState, AddData } from '../../Redux/Actions/actions.js';
import Select from 'react-select';

class CreateMaterialModal extends React.PureComponent {
  render() {
    var data = this.props.pagedTablesData.materials;
    var materialInfoArea;
    var actionButtons;
    var materialToCreate = {
      materialName: '',
      materialAmount: '',
      productionDate: '',
      bestBeforeDate: '',
      materialManufacturer: '',
    };

    // Array for using in <Select /> dropdown list
    var manufacturers = [];
    data.manufacturerSelectList.forEach((element) => {
      manufacturers.push({ value: element.value, label: element.text });
    });

    // Elements to render conrent
    actionButtons = (
      <div className="confirmUserInfo">
        <button
          className="modalButton"
          onClick={() => {
            this.props.setCreateModalState();
            this.props.onCreate(this.props.activeMenu, materialToCreate);
          }}
        >
          Create new marerial
        </button>
        <button
          className="modalButton"
          onClick={() => {
            this.props.setCreateModalState();
          }}
        >
          Add new manufacturer
        </button>
      </div>
    );

    materialInfoArea = (
      <div className="profileText">
        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Material name:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="text"
              placeholder="Material name..."
              onChange={(e) => {
                materialToCreate.materialName = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Material amount:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="text"
              placeholder="Material amount..."
              onChange={(e) => {
                materialToCreate.materialAmount = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Production date:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="date"
              placeholder="Phone number..."
              onChange={(e) => {
                materialToCreate.productionDate = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Best before date:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="date"
              placeholder="Best before date..."
              onChange={(e) => {
                materialToCreate.bestBeforeDate = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Manufacturer:</p>
          </div>
          <div className="profileTextItem-right">
            <Select
              options={manufacturers}
              onChange={(data) => {
                this.props.responseDataList.manufacturers.forEach((element) => {
                  if (element.manufacturerName === data.label) {
                    materialToCreate.materialManufacturer = element;
                  }
                });
              }}
            />
          </div>
        </div>
      </div>
    );

    return (
      <Modal
        styles={{
          modal: {
            maxWidth: 'fit-content',
            background: '#6c3636',
            padding: '40px',
            fontFamily: 'Play',
            alignItems: 'center',
            textAlign: 'center',
            border: '6px solid #f7f6f6',
            borderRadius: '10px',
          },
        }}
        open={this.props.createModalOpen}
        center={true}
        showCloseIcon={false}
        onOverlayClick={this.props.setCreateModalState}
      >
        <>
          <div className="profileBody">{materialInfoArea}</div>
          {actionButtons}
        </>
      </Modal>
    );
  }
}

function mapStateToProps(state) {
  return {
    createModalOpen: state.createModalOpen,
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setCreateModalState: () => dispatch(SetCreateModalState()),
    onCreate: (activeMenu, data) => dispatch(AddData(activeMenu, data)),
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(CreateMaterialModal);
