import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../../Redux/Actions/actions.js';
import CreateOrderModal from '../ModalWindowsComponents/CreateOrderModal.js';
import UpdateOrderModal from '../ModalWindowsComponents/UpdateOrderModal.js';

class OrdersList extends React.PureComponent {
  render() {
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>Date of service</th>
            <th>Client name</th>
            <th>Client phone number</th>
            <th>Client email</th>
            <th>Procedure name</th>
            <th>Procedure cost</th>
            <th>Employee</th>
          </tr>
        </thead>
      );

      for (let orderInfo in this.props.responseDataList) {
        element = '';
        // element = (
        //   <tr
        //     key={orderInfo}
        //     id={orderInfo}
        //     onClick={async () => {
        //       this.props.setActiveId(orderInfo);
        //       await this.props.getOrder(orderInfo, this.props.activeMenu);
        //       this.props.setUpdateModalState();
        //       this.props.getSchedule(
        //         this.props.responseDataList[orderInfo][7],
        //         this.props.responseDataList[orderInfo][0].substr(0, 10),
        //         this.props.responseDataList[orderInfo][8],
        //         this.props.openTime,
        //         this.props.closeTime,
        //       );
        //     }}
        //   >
        //     <td>{this.props.responseDataList[orderInfo][0].substr(0, 16)}</td>
        //     <td>{this.props.responseDataList[orderInfo][1]}</td>
        //     <td>{this.props.responseDataList[orderInfo][2]}</td>
        //     <td>{this.props.responseDataList[orderInfo][3]}</td>
        //     <td>{this.props.responseDataList[orderInfo][4]}</td>
        //     <td>{this.props.responseDataList[orderInfo][5]}</td>
        //     <td>{this.props.responseDataList[orderInfo][6]}</td>
        //   </tr>
        // );
        elements = elements.concat(element);
      }

      if (this.props.responseDataList !== '') {
        return (
          <>
            <button
              className="modalButton"
              onClick={() => {
                this.props.setCreateModalState();
              }}
            >
              New order
            </button>
            <div className="dataTable">
              <table className="tableStyle">
                {header}
                <tbody>{elements}</tbody>
                {/* <CreateOrderModal />
              <UpdateOrderModal /> */}
              </table>
            </div>
          </>
        );
      } else {
        return (
          <>
            <p>There is no Data in DB...</p>
          </>
        );
      }
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
    openTime: state.openTime,
    closeTime: state.closeTime,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setUpdateModalState: () => dispatch(actions.SetUpdateModalState()),
    setCreateModalState: () => dispatch(actions.SetCreateModalState()),
    setActiveId: (id) => dispatch(actions.SetActiveIdentifier(id)),
    getOrder: (id, activeMenu) =>
      dispatch(actions.FetchSingleData(id, activeMenu)),
    getSchedule: (employeeId, dateOfService, procedureId, open, close) =>
      dispatch(
        actions.GetAvaliableTimes(
          employeeId,
          dateOfService,
          procedureId,
          open,
          close,
        ),
      ),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(OrdersList);
