import React from 'react';
import {
  EmployeeAutorization,
  ResetActiveMenu,
} from '../../Redux/Actions/actions.js';
import { connect } from 'react-redux';
import { onSubmit } from './LoginModal';
import Modal from 'react-responsive-modal';

class Autorization extends React.PureComponent {
  render() {
    return (
      <Modal
        styles={{
          modal: {
            maxWidth: 'fit-content',
            background: '#6c3636',
            padding: '40px',
            fontFamily: 'Play',
            alignItems: 'center',
            textAlign: 'center',
            border: '6px solid #f7f6f6',
            borderRadius: '10px',
            height: '300px',
          },
        }}
        open={this.props.createModalOpen}
        center={true}
        showCloseIcon={false}
        onOverlayClick={() => {
          this.props.setCreateModalState();
          this.props.setActiveId('');
        }}
      >
        <form
          onSubmit={(e) => {
            e.preventDefault();
            this.props.resetMenu();
            this.props.onAuth(
              document.getElementById('employeeEmail').value,
              document.getElementById('employeePassword').value,
            );
            onSubmit();
          }}
        >
          <p>Email: </p>
          <input
            type="email"
            className="employeeCredentials"
            id="employeeEmail"
          />
          <p>Password: </p>
          <input
            type="password"
            className="employeeCredentials"
            id="employeePassword"
          />
          <div>
            <input type="submit" value="Login" className="modalButton" />
          </div>
        </form>
      </Modal>
    );
  }
}

function mapDispatchToProps(dispatch) {
  return {
    resetMenu: () => dispatch(ResetActiveMenu()),
    onAuth: (email, password) =>
      dispatch(EmployeeAutorization(email, password)),
  };
}

export default connect(null, mapDispatchToProps)(Autorization);
