import * as TYPES from './Actions/actionTypes.js';

const initialState = {
  employeeToken: '',
  isAuthenticated: false,
  isEditing: false,
  open: false,
  responseDataList: '',
  singleResponseData: '',
  activeMenu: '',
  activeId: '',
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case TYPES.ACTIVE_ID_SET:
      return {
        ...state,
        activeId: action.payload,
      };
    case TYPES.FETCH_DATA_SUCCESS:
      return {
        ...state,
        activeMenu: action.type,
        responseDataList: action.payload,
      };

    // Staff reducers ---------------------------------------

    case TYPES.EMPLOYEE_AUTORIZATION_SUCCESS:
      return {
        ...state,
        employeeToken: action.payload,
        isAuthenticated: true,
      };

    case TYPES.EMPLOYEE_LOGOUT:
      return {
        ...state,
        employeeToken: '',
        isAuthenticated: false,
      };

    case TYPES.MODAL_WINDOW_STATE:
      return {
        ...state,
        open: !state.open,
      };

    case TYPES.EDIT_STATE:
      return {
        ...state,
        isEditing: !state.isEditing,
      };

    default:
      return state;
  }
}
