import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../../Redux/Actions/actions.js';
import CreateServiceModal from '../ModalWindowsComponents/CreateServiceModal.js';
import UpdateServiceModal from '../ModalWindowsComponents/UpdateServiceModal';

class ServicesList extends React.PureComponent {
  render() {
    var data = this.props.pagedTablesData;
    if (data !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>Name</th>
            <th>Type</th>
            <th>Time amount</th>
            <th>Price</th>
          </tr>
        </thead>
      );

      for (let i = 0; i < data.length; i++) {
        element = '';
        element = (
          <tr
            key={data[i].procedureId}
            id={data[i].procedureId}
            onClick={() => {
              this.props.setActiveId(data[i].procedureId);
              this.props.getService(data[i].procedureId, this.props.activeMenu);
              this.props.setUpdateModalState();
            }}
          >
            <td>{data[i].procedureName}</td>
            <td>{data[i].procedureType}</td>
            <td>{this.props.responseDataList.procedures[i].timeAmount}</td>
            <td>{this.props.responseDataList.procedures[i].procedurePrice}</td>
          </tr>
        );
        elements = elements.concat(element);
      }
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <button
            className="modalButton"
            onClick={() => {
              this.props.setCreateModalState();
            }}
          >
            New service
          </button>
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
            </table>
            {/* <CreateServiceModal />
            <UpdateServiceModal /> */}
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setUpdateModalState: () => dispatch(actions.SetUpdateModalState()),
    setCreateModalState: () => dispatch(actions.SetCreateModalState()),
    setActiveId: (id) => dispatch(actions.SetActiveIdentifier(id)),
    getService: (id, activeMenu) =>
      dispatch(actions.FetchSingleData(id, activeMenu)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(ServicesList);
