import {
  EMPLOYEE_AUTORIZATION_SUCCESS,
  CLIENTS_FETC_DATA_SUCCESS,
  ACTIVE_ID_SET,
} from './actionTypes';

//Active identifier setting------------------------------

export function SetActiveIdentifier(id) {
  console.log('id =', id);
  return {
    type: ACTIVE_ID_SET,
    payload: id,
  };
}

// List of clients----------------------------------------

export function ClientsFeetchData() {
  console.log('feetching starts');
  const requestURL = 'https://localhost:7275/Client/GetAllClients';
  return (dispatch) => {
    fetch(requestURL)
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response;
      })
      .then(async (response) => {
        const data = await response.json();
        console.log('data: ', data);
        dispatch(ClientsFeetchDataSuccess(data));
      });
  };
}

export function ClientsFeetchDataSuccess(data) {
  console.log('on success ', data);
  return {
    type: CLIENTS_FETC_DATA_SUCCESS,
    payload: data,
  };
}

// Authorization of employees -----------------------------------------------------------------------------

export function EmployeeAutorization(email, password) {
  const requestURL = `https://localhost:7275/Employee/GetToken?employeeEmail=${email}&password=${password}`;
  return (dispatch) => {
    fetch(requestURL, {
      method: 'post',
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response;
      })
      .then(async (response) => {
        const data = await response.json();
        dispatch(EmployeeAutorizationSuccess(data));
      });
  };
}

export function EmployeeAutorizationSuccess(data) {
  console.log('before reducer', data);
  return {
    type: EMPLOYEE_AUTORIZATION_SUCCESS,
    payload: data,
  };
}
