import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../../Redux/Actions/actions.js';
import CreateEmployeeModal from '../ModalWindowsComponents/CreateEmployeeModal.js';
import UpdateEmployeeModal from '../ModalWindowsComponents/UpdateEmployeeModal.js';

class EmployeeList extends React.PureComponent {
  render() {
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      var photos = [];

      for (let i = 0; i < this.props.responseDataList.employees.length; i++) {
        console.log(
          typeof this.props.responseDataList.employees[0].mediaFiles[0],
        );
        // const blob =
        //   this.props.responseDataList.employees[0].mediaFiles[0].blob();
        // const test = URL.createObjectURL(blob);
        photos.push(
          this.props.getProfilePhotoByEmployeeId(
            this.props.responseDataList.employees[i].employeeId,
          ),
        );
        element = '';
        element = (
          <dir
            className="profileCardBody"
            key={this.props.responseDataList.employees[i].employeeId}
            id={this.props.responseDataList.employees[i].employeeId}
            onClick={() => {
              this.props.setActiveId(
                this.props.responseDataList.employees[i].employeeId,
              );
              this.props.getEmployee(
                this.props.responseDataList.employees[i].employeeId,
                this.props.activeMenu,
              );
              this.props.setUpdateModalState();
              this.props.getProfilePhotoByEmployeeId(
                this.props.responseDataList.employees[i].employeeId,
              );
            }}
          >
            <div className="profileCardInfo">
              <p>
                {this.props.responseDataList.employees[i].firstName +
                  ' ' +
                  this.props.responseDataList.employees[i].lastName}
              </p>
              <div className="profileCardPhoto">
                {/* <img src={test} alt="ololololo" /> */}
              </div>
            </div>
          </dir>
        );
        elements = elements.concat(element);
      }
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <button
            className="modalButton"
            onClick={() => {
              this.props.setCreateModalState();
            }}
          >
            New employee
          </button>
          <div className="dataTable">
            <table className="tableStyle">
              <tbody>{elements}</tbody>
              <CreateEmployeeModal />
              <UpdateEmployeeModal />
            </table>
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setUpdateModalState: () => dispatch(actions.SetUpdateModalState()),
    setCreateModalState: () => dispatch(actions.SetCreateModalState()),
    setActiveId: (id) => dispatch(actions.SetActiveIdentifier(id)),
    getEmployee: (id, activeMenu) =>
      dispatch(actions.FetchSingleData(id, activeMenu)),
    getProfilePhotoByEmployeeId: (id) =>
      dispatch(actions.GetProfilePhotoByEmployeeId(id)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(EmployeeList);
