import React from 'react';
import { connect } from 'react-redux';
import { GetImage, SetActiveIdentifier } from '../../Redux/Actions/actions.js';
import Select from 'react-select';

class MediaFilesList extends React.PureComponent {
  render() {
    var data = this.props.pagedTablesData;
    var employeeList = [];

    data.employeesSelectList.forEach((element) => {
      employeeList.push({
        value: element.value,
        label: element.text,
      });
    });

    if (data !== '') {
      var elements = [];
      var element;
      if (data.mediafiles.length > 0) {
        for (let i = 0; i < data.length; i++) {
          element = [];
          element = (
            <dir
              className="profileCardBody"
              key={data.mediafiles[i].fileContents}
              id={data.mediafiles[i].fileContents}
              onClick={() => {
                this.props.setUpdateModalState();
              }}
            >
              <div className="profileCardInfo">
                <div className="profilePhoto">
                  <img
                    src={`data:image/gif;base64,${data.mediafiles[i].fileContents}`}
                    alt=""
                  />
                </div>
              </div>
            </dir>
          );
          elements = elements.concat(element);
        }
      }
    }

    if (data !== '') {
      return (
        <>
          <div className="tableFunctionality">
            <p className="title">Mediafiles</p>
            <Select
              maxMenuHeight={100}
              options={employeeList}
              placeholder="Choose the employee..."
              // onChange={(data) => {
              //   employeeToUpdate.qualification = data.value;
              // }}
            />
          </div>
          <div className="dataTable">
            <div className="tableStyle media">{elements}</div>
          </div>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    pagedTablesData: state.pagedTablesData,
    singleResponseData: state.singleResponseData,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getImage: (id) => dispatch(GetImage(id)),
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(MediaFilesList);
