import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../../Redux/Actions/actions.js';
import StyleChange from '../../Functions/ChangeStyles.js';

class ManagementPage extends React.PureComponent {
  render() {
    return (
      <nav className="navigationBar">
        <ul>
          <li>
            <button
              className="managementMenuButton"
              id="staffPage"
              onClick={(e) => {
                this.props.resetSingleData();
                this.props.getAll(e.target.id);
                this.props.setOrderCreationStatus();
                StyleChange(e.target.id);
              }}
            >
              Staff
            </button>
          </li>

          <li>
            <button
              className="managementMenuButton"
              id="clientsPage"
              onClick={(e) => {
                this.props.resetSingleData();
                this.props.getAll(e.target.id);
                StyleChange(e.target.id);
              }}
            >
              Clients
            </button>
          </li>

          <li>
            <button
              className="managementMenuButton"
              id="servicesPage"
              onClick={(e) => {
                this.props.resetSingleData();
                this.props.getAll(e.target.id);
                StyleChange(e.target.id);
              }}
            >
              Services
            </button>
          </li>

          <li>
            <button
              className="managementMenuButton"
              id="materialsPage"
              onClick={(e) => {
                this.props.resetSingleData();
                this.props.getAll(e.target.id);
                StyleChange(e.target.id);
              }}
            >
              Materials
            </button>
          </li>

          <li>
            <button
              className="managementMenuButton"
              id="ordersPage"
              onClick={(e) => {
                this.props.resetSingleData();
                this.props.getAll(e.target.id);
                StyleChange(e.target.id);
              }}
            >
              Active orders
            </button>
          </li>

          <li>
            <button
              className="managementMenuButton"
              id="donedOrdersPage"
              onClick={(e) => {
                this.props.resetSingleData();
                this.props.getAllCompleted(e.target.id);
                StyleChange(e.target.id);
              }}
            >
              Completed orders
            </button>
          </li>

          <li>
            <button
              className="managementMenuButton"
              id="mediaFilesPage"
              onClick={(e) => {
                this.props.resetSingleData();
                this.props.getAllMedia(e.target.id, 'all', 0);
                StyleChange(e.target.id);
              }}
            >
              Files
            </button>
          </li>
          <li>
            <button
              className="managementMenuButton"
              id="feedbacksPage"
              onClick={(e) => {
                this.props.resetSingleData();
                this.props.getAll(e.target.id);
                StyleChange(e.target.id);
              }}
            >
              Feedbacks
            </button>
          </li>
        </ul>
      </nav>
    );
  }
}

function mapStateToProps(state) {
  return {
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getAll: (id) => dispatch(actions.FeetchMainTableData(id)),
    getAllCompleted: (id) => dispatch(actions.FetchAllDonedOrders(id)),
    getAllMedia: (activeMenu, fileType, employeeId) =>
      dispatch(actions.FeetchAllMediafiles(activeMenu, fileType, employeeId)),
    resetSingleData: () => dispatch(actions.ResetActiveSingleData()),
    setOrderCreationStatus: () => dispatch(actions.SetOrderStatus()),
  };
}
export default connect(mapStateToProps, mapDispatchToProps)(ManagementPage);
