import React from 'react';
import { connect } from 'react-redux';
import {
  SetActiveIdentifier,
  SetModalState,
  EmployeeFetchData,
} from '../../Redux/Actions/actions.js';
import EmployeeModal from '../Modals/EmployeeModal.js';

class EmployeeList extends React.PureComponent {
  render() {
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>FirstName</th>
            <th>Lastname</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Specialization</th>
            <th>Qualification</th>
          </tr>
        </thead>
      );

      for (let i = 0; i < this.props.responseDataList.length; i++) {
        element = '';
        element = (
          <tr
            key={this.props.responseDataList[i].employeeId}
            id={this.props.responseDataList[i].employeeId}
            onClick={() => {
              this.props.setActiveId(this.props.responseDataList[i].employeeId);
              this.props.getEmployee(this.props.responseDataList[i].employeeId);
              this.props.setModalState();
            }}
          >
            <td>{this.props.responseDataList[i].firstName}</td>
            <td>{this.props.responseDataList[i].lastName}</td>
            <td>{this.props.responseDataList[i].phoneNumber}</td>
            <td>{this.props.responseDataList[i].email}</td>
            <td>{this.props.responseDataList[i].specialization}</td>
            <td>{this.props.responseDataList[i].qualification}</td>
          </tr>
        );
        elements = elements.concat(element);
      }
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
              <EmployeeModal />
            </table>
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    activeId: state.activeId,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setModalState: () => dispatch(SetModalState()),
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
    getEmployee: (id) => dispatch(EmployeeFetchData(id)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(EmployeeList);
