import React from 'react';
import ReactDOM from 'react-dom';
import { connect } from 'react-redux';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';
import { SetModalState } from '../../Redux/Actions/actions.js';
class EmployeeModal extends React.PureComponent {
  render() {
    return (
      <Modal
        styles={{
          modal: {
            background: 'wheat',
            padding: '40px',
            fontFamily: 'Nautigal-Regular',
            alignItems: 'center',
            textAlign: 'center',
            fontSize: '45px',
            border: '3px solid black',
            borderRadius: '10px',
          },
        }}
        open={this.props.open}
        center={true}
        showCloseIcon={false}
        onOverlayClick={this.props.setModalState}
        onEscKeyDown={this.props.SetModalState}
      >
        <>
          <div>
            <div className="profilePhoto">
              <img src="../../../public/images/example3.jpg" alt="profile" />
            </div>
          </div>

          <div>
            <button className="modalButton">Information</button>
            <button>Delete employee</button>
          </div>
        </>
      </Modal>
    );
  }
}

function mapStateToProps(state) {
  return {
    open: state.open,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setModalState: () => dispatch(SetModalState()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(EmployeeModal);
