import React from 'react';
import ReactDOM from 'react-dom';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import { connect } from 'react-redux';
import { Carousel } from 'react-responsive-carousel';

class NewsCarousel extends React.PureComponent {
  render() {
    return (
      <>
        <div className="title">Promotions</div>
        <Carousel
          width={450}
          className="carousel"
          dynamicHeight={true}
          width={200}
        >
          <div>
            <img
              className="img-container"
              src="./images/promo/1.jpg"
              alt="some news here"
              class="news-item"
            />
          </div>
          <div>
            <img
              className="img-container"
              src="./images/promo/2.jpg"
              alt="some news here"
              class="news-item"
            />
          </div>
          <div>
            <img
              className="img-container"
              src="./images/promo/3.jpg"
              alt="some news here"
              class="news-item"
            />
          </div>
        </Carousel>
      </>
    );
  }
}
export default connect()(NewsCarousel);
