import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../Redux/Actions/actions';
import ManagementPage from './DataTablesComponents/Management';
import { LoginModal } from './ModalWindowsComponents/LoginModal';
import StyleChange from '../Functions/ChangeStyles';
import CreateFeedbackModal from './ModalWindowsComponents/CreateFeedbackModal';
import CreateOrderModal from './ModalWindowsComponents/CreateOrderModal';

class NavigationBar extends React.PureComponent {
  render() {
    if (this.props.employeeToken === '') {
      return (
        <nav className="navigationBar">
          <ul>
            <li>
              <button
                className="managementMenuButton"
                id="ourServices"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                Our services
              </button>
            </li>
            <li>
              <button
                className="managementMenuButton"
                id="ourStaff"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                Our staff
              </button>
            </li>
            <li>
              <button
                className="managementMenuButton"
                id="feedback"
                onClick={(e) => {
                  StyleChange(e.target.id);
                  this.props.setCreateModalState();
                  this.props.getAllClients('clientsPage');
                }}
              >
                Feedback
              </button>
            </li>
            <li>
              <button
                className="managementMenuButton"
                id="reviews"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                Reviews
              </button>
            </li>
            <li>
              <button
                className="managementMenuButton"
                id="aboutUs"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                About us
              </button>
            </li>
            <li>
              <LoginModal />
              <CreateFeedbackModal />
            </li>
          </ul>
          <button
            className="booking"
            onClick={() => {
              this.props.getAllOrders('ordersPage');
              this.props.setBookingState();
            }}
          >
            Book now
          </button>
        </nav>
      );
    } else {
      return <ManagementPage />;
    }
  }
}

function mapStateToProps(state) {
  return {
    employeeToken: state.employeeToken,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getAllClients: (activePage) => dispatch(actions.FeetchAllData(activePage)),
    getAllOrders: (activePage) => dispatch(actions.FeetchAllData(activePage)),
    setCreateModalState: () => dispatch(actions.SetCreateModalState()),
    setBookingState: () => dispatch(actions.SetBookingStatus()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(NavigationBar);
