import React from 'react';
import { connect } from 'react-redux';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';
import { SetCreateModalState, AddData } from '../../Redux/Actions/actions.js';

class CreateClientModal extends React.PureComponent {
  render() {
    var materialInfoArea;
    var actionButtons;
    var materialToCreate = {
      materialName: '',
      materialAmount: '',
      productionDate: '',
      bestBeforeDate: '',
    };

    actionButtons = (
      <div className="confirmUserInfo">
        <div>
          <input type="file" />
        </div>
        <div>
          <button
            className="modalButton"
            onClick={() => {
              this.props.setCreateModalState();
              this.props.onCreate(this.props.activeMenu, materialToCreate);
            }}
          >
            Create
          </button>
        </div>
      </div>
    );

    materialInfoArea = (
      <div className="profileText">
        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Material name:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="text"
              placeholder="Material name..."
              onChange={(e) => {
                materialToCreate.materialName = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Material amount:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="text"
              placeholder="Material amount..."
              onChange={(e) => {
                materialToCreate.materialAmount = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Production date:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="date"
              placeholder="Phone number..."
              onChange={(e) => {
                materialToCreate.productionDate = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Best before date:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="date"
              placeholder="Best before date..."
              onChange={(e) => {
                materialToCreate.bestBeforeDate = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Best before date:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="date"
              placeholder="Best before date..."
              onChange={(e) => {
                materialToCreate.bestBeforeDate = e.target.value;
              }}
            />
          </div>
        </div>
      </div>
    );

    return (
      <Modal
        styles={{
          modal: {
            background: 'wheat',
            padding: '40px',
            fontFamily: 'Play',
            alignItems: 'center',
            textAlign: 'center',
            fontSize: '45px',
            border: '3px solid black',
            borderRadius: '10px',
          },
        }}
        open={this.props.createModalOpen}
        center={true}
        showCloseIcon={false}
        onOverlayClick={this.props.setCreateModalState}
      >
        <>
          <div className="profileBody">{materialInfoArea}</div>
          {actionButtons}
        </>
      </Modal>
    );
  }
}

function mapStateToProps(state) {
  return {
    createModalOpen: state.createModalOpen,
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setCreateModalState: () => dispatch(SetCreateModalState()),
    onCreate: (activeMenu, data) => dispatch(AddData(activeMenu, data)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateClientModal);
