import React from 'react';
import { connect } from 'react-redux';
import {
  SetActiveIdentifier,
  SetModalState,
  EmployeeFetchData,
} from '../../Redux/Actions/actions.js';
import EmployeeModal from '../Modals/EmployeeModal.js';

class EmployeeList extends React.PureComponent {
  render() {
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>FirstName</th>
            <th>Lastname</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Role</th>
            <th>Specialization</th>
            <th>Qualification</th>
          </tr>
        </thead>
      );

      for (let i = 0; i < this.props.responseDataList.employees.length; i++) {
        var role;
        var specialization;
        var qualification;

        for (
          let j = 0;
          j < this.props.responseDataList.specialization.length;
          j++
        ) {
          if (
            this.props.responseDataList.specialization[j].value ===
            this.props.responseDataList.employee[i].specialization
          ) {
            specialization = this.props.responseDataList.specialization[j].name;
          }
        }

        //----------------------------------------------------
        for (
          let k = 0;
          k < this.props.responseDataList.qualification.length;
          k++
        ) {
          if (
            this.props.responseDataList.employees[i].qualification ===
            this.props.responseDataList.qualification[k].value
          ) {
            qualification = this.props.responseDataList.qualification[k].name;
          }
        }

        //----------------------------------------------------
        for (let n = 0; n < this.props.responseDataList.roles.length; n++) {
          if (
            this.props.responseDataList.employees[i].role ===
            this.props.responseDataList.roles[n].value
          ) {
            role = this.props.responseDataList.roles[n].name;
          }
        }

        console.log(specialization);
        element = '';
        element = (
          <tr
            key={this.props.responseDataList.employees[i].employeeId}
            id={this.props.responseDataList.employees[i].employeeId}
            onClick={() => {
              this.props.setActiveId(
                this.props.responseDataList.employees[i].employeeId,
              );
              this.props.getEmployee(
                this.props.responseDataList.employees[i].employeeId,
              );
              this.props.setModalState();
            }}
          >
            <td>{this.props.responseDataList.employees[i].firstName}</td>
            <td>{this.props.responseDataList.employees[i].lastName}</td>
            <td>{this.props.responseDataList.employees[i].phoneNumber}</td>
            <td>{this.props.responseDataList.employees[i].email}</td>
            <td>{role}</td>
            {/* <td>{specialization}</td> */}
            <td>{qualification}</td>
          </tr>
        );
        elements = elements.concat(element);
      }
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
              <EmployeeModal />
            </table>
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    checkStateForRender: state.checkStateForRender,
    responseDataList: state.responseDataList,
    activeId: state.activeId,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setModalState: () => dispatch(SetModalState()),
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
    getEmployee: (id) => dispatch(EmployeeFetchData(id)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(EmployeeList);
