import * as TYPES from './actionTypes';

// Authorization of employees -----------------------------------------------------------------------------

export function EmployeeAutorization(email, password) {
  const requestURL = `https://localhost:7275/Employee/GetToken?employeeEmail=${email}&password=${password}`;
  return (dispatch) => {
    fetch(requestURL, {
      method: 'post',
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response;
      })
      .then(async (response) => {
        const data = await response.json();
        dispatch(EmployeeAutorizationSuccess(data));
      });
  };
}

export function EmployeeAutorizationSuccess(data) {
  return {
    type: TYPES.EMPLOYEE_AUTORIZATION_SUCCESS,
    payload: data,
  };
}

export function EmployeeLogout() {
  return {
    type: TYPES.EMPLOYEE_LOGOUT,
  };
}

//Active identifier setting------------------------------

export function SetActiveIdentifier(id) {
  return {
    type: TYPES.ACTIVE_ID_SET,
    payload: id,
  };
}

// Data fetching -----------------------------------------

//Get ALL depending on active menu
export function FeetchAllData(activeMenu) {
  var requestURL;
  switch (activeMenu) {
    case 'clientsPage':
      requestURL = 'https://localhost:7275/Client/GetAllClients';
      break;
    case 'materialsPage':
      requestURL = 'https://localhost:7275/Material/GetAllMaterials';
      break;
    case 'servicesPage':
      requestURL = 'https://localhost:7275/Procedure/GetAllProcedures';
      break;
    case 'ordersPage':
      requestURL = 'https://localhost:7275/Order/GetAllOrders';
      break;
    case 'profilesPage':
      requestURL = 'https://localhost:7275/Profile/GetAllProfiles';
      break;
    case 'promoPage':
      requestURL = 'https://localhost:7275/Profile/GetAllProfiles';
      break;
    case 'feedbacksPage':
      requestURL = 'https://localhost:7275/Feedback/GetAllFeedbacks';
      break;
    case 'staffPage':
      requestURL = 'https://localhost:7275/Employee/GetAllEmployees';
      break;
    case 'mediaFilesPage':
      requestURL = `https://localhost:7275/MediaFile/GetAllMediaFiles`;
      break;
    default:
      break;
  }
  return (dispatch) => {
    fetch(requestURL)
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response;
      })
      .then(async (response) => {
        const data = await response.json();
        dispatch(FeetchAllDataSuccess(data, activeMenu));
      });
  };
}

export function FeetchAllDataSuccess(data, id) {
  return {
    type: TYPES.FETCH_DATA_SUCCESS,
    payload: data,
    identifier: id,
  };
}

//Get by id depending on id and active menu----------------------------------------------------

export function FetchSingleData(id, activeMenu) {
  console.log(activeMenu);
  console.log(id);
  var requestURL;
  switch (activeMenu) {
    case 'clientsPage':
      requestURL = `https://localhost:7275/Client/GetClientById?id=${id}`;
      break;
    case 'materialsPage':
      requestURL = `https://localhost:7275/Material/GetMaterialById?id=${id}`;
      break;
    case 'servicesPage':
      requestURL = `https://localhost:7275/Procedure/GetServiceById?id=${id}`;
      break;
    case 'ordersPage':
      requestURL = `https://localhost:7275/Order/GetOrderById?id=${id}`;
      break;
    case 'profilesPage':
      requestURL = `https://localhost:7275/Profile/GetProfileById?id=${id}`;
      break;
    case 'promoPage':
      requestURL = `https://localhost:7275/Profile/GetProfileById?id=${id}`;
      break;
    case 'feedbacksPage':
      requestURL = `https://localhost:7275/Feedback/GetFeedbackById?id=${id}`;
      break;
    case 'staffPage':
      requestURL = `https://localhost:7275/Employee/GetEmployeeById?id=${id}`;
      break;
    case 'mediaFilesPage':
      requestURL = `https://localhost:7275/MediaFile/GetFileById?id=${id}`;
      break;

    default:
      break;
  }

  console.log(requestURL);
  return (dispatch) => {
    fetch(requestURL)
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response;
      })
      .then(async (response) => {
        const data = await response.json();
        dispatch(FeetchSingleDataSuccess(data));
      });
  };
}

export function FeetchSingleDataSuccess(data) {
  return {
    type: TYPES.SINGLE_FETCH_DATA_SUCCESS,
    payload: data,
  };
}

//--------------------------------------------------------

// States processing functions

export function SetEditState() {
  return {
    type: TYPES.EDIT_STATE,
  };
}

export function SetUpdateModalState() {
  return {
    type: TYPES.UPDATE_MODAL_WINDOW_STATE,
  };
}

export function SetCreateModalState() {
  return {
    type: TYPES.CREATE_MODAL_WINDOW_STATE,
  };
}

//Delete data depending on id and active menu

export function DeleteData(id, activeMenu) {
  var requestURL;
  switch (activeMenu) {
    case 'clientsPage':
      requestURL = `https://localhost:7275/Client/DeleteClientById?id=${id}`;
      break;
    case 'materialsPage':
      requestURL = `https://localhost:7275/Material/DeleteMaterialById?id=${id}`;
      break;
    case 'servicesPage':
      requestURL = `https://localhost:7275/Procedure/DeleteServiceById?id=${id}`;
      break;
    case 'ordersPage':
      requestURL = `https://localhost:7275/Order/DeleteOrderById?id=${id}`;
      break;
    case 'profilesPage':
      requestURL = `https://localhost:7275/Profile/DeleteProfileById?id=${id}`;
      break;
    case 'promoPage':
      requestURL = `https://localhost:7275/Profile/DeleteProfileById?id=${id}`;
      break;
    case 'feedbacksPage':
      requestURL = `https://localhost:7275/Feedback/DeleteFeedbackById?id=${id}`;
      break;
    case 'staffPage':
      requestURL = `https://localhost:7275/Employee/DeleteEmployeeById?id=${id}`;
      break;
    default:
      break;
  }
  return (dispatch) => {
    fetch(requestURL, { method: 'post', cors: 'no-cors' }).then(() =>
      dispatch(FeetchAllData(activeMenu)),
    );
  };
}

// Update data depending on id and active menu---------------------------------

