import React from 'react';
import StyleChange from '../../../Functions/ChangeStyles';
import * as actions from '../../../Redux/Actions/actions';
import { connect } from 'react-redux';
import Select from 'react-select';

class MediaFileTypes extends React.PureComponent {
  render() {
    var data = this.props.pagedTablesData;
    var employeeList = [];
    var filetype = 'profile';

    data.employeesSelectList.forEach((element) => {
      employeeList.push({
        value: element.value,
        label: element.text,
      });
    });

    return (
      <div>
        <p style={{ color: 'wheat' }}>Mediafiles type: </p>
        <button
          className="recordsPerPage files"
          id="profile"
          onClick={(e) => {
            this.props.getOrders(this.props.activeMenu, 100, 1, e.target.id);
            filetype = 'profile';
            StyleChange(e.target.id, 'recordsPerPage');
          }}
        >
          Profile photo
        </button>
        <button
          className="recordsPerPage files"
          id="employeeGallery"
          onClick={(e) => {
            this.props.getOrders(this.props.activeMenu, 100, 1, e.target.id);
            filetype = 'employeeGallery';
            StyleChange(e.target.id);
          }}
        >
          Employee gallery
        </button>
        <Select
          maxMenuHeight={100}
          options={employeeList}
          placeholder="Choose the employee..."
          onChange={(data) => {
            this.props.getOrders(
              this.props.activeMenu,
              100,
              1,
              filetype,
              data.value,
            );
          }}
        />
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    pagedTablesData: state.pagedTablesData,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getOrders: (menu, rowsPerPage, pageNumber, type, id) =>
      dispatch(
        actions.FeetchMainTableData(menu, rowsPerPage, pageNumber, type, id),
      ),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(MediaFileTypes);
