import React from 'react';
import { connect } from 'react-redux';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';
import {
  SetModalState,
  SetEditState,
  DeleteData,
  UpdateEmployee,
} from '../../Redux/Actions/actions.js';
import Select from 'react-select';
import testImage from '../../temporaryFiles/example3.jpg';

class EmployeeModal extends React.PureComponent {
  render() {
    var date = this.props.singleResponseData.hireDate;
    var firstName = `${this.props.singleResponseData.firstName}`;
    var lastName = `${this.props.singleResponseData.lastName}`;
    var phone = this.props.singleResponseData.phoneNumber;
    var email = this.props.singleResponseData.email;
    var employeeInfoArea;
    var actionButtons;
    var employeeToUpdate = {
      id: this.props.activeId,
      firstName: '',
      lastName: '',
      phone: '',
      email: '',
      specialization: '',
      qualification: '',
      role: '',
      hired: '',
    };

    var roles = [];
    this.props.responseDataList.roles.forEach((element) => {
      roles.push({ value: element.value, label: element.name });
    });

    var qualifications = [];
    this.props.responseDataList.qualification.forEach((element) => {
      qualifications.push({ value: element.value, label: element.name });
    });

    var specializations = [];
    this.props.responseDataList.specialization.forEach((element) => {
      specializations.push({ value: element.value, label: element.name });
    });

    if (!this.props.isEditing) {
      actionButtons = (
        <div className="editUserInfo">
          <button className="modalButton" onClick={this.props.onEdit}>
            Edit profile
          </button>
          <button
            className="modalButton"
            onClick={() => {
              this.props.onDelete(this.props.activeId, this.props.activeMenu);
              this.props.setModalState();
            }}
          >
            Delete profile
          </button>
        </div>
      );

      employeeInfoArea = (
        <div className="profileText">
          <div className="profileTextItem">
            <p>First name: {firstName}</p>
          </div>

          <div className="profileTextItem">
            <p>Last name: {lastName}</p>
          </div>

          <div className="profileTextItem">
            <p>Hired: {date}</p>
          </div>

          <div className="profileTextItem">
            <p>Phone: {phone}</p>
          </div>

          <div className="profileTextItem">
            <p>Email: {email}</p>
          </div>

          <div className="profileTextItem">
            <p>Specialization:</p>
          </div>

          <div className="profileTextItem">
            <p>Qualification: </p>
          </div>

          <div className="profileTextItem">
            <p>Role: </p>
          </div>
        </div>
      );
    } else {
      actionButtons = (
        <div className="confirmUserInfo">
          <div>
            <input type="file" />
          </div>
          <div>
            <button
              className="modalButton"
              onClick={() => {
                this.props.onEdit();
                this.props.onUpdate(this.props.activeMenu, employeeToUpdate);
              }}
            >
              Save changes
            </button>
          </div>
        </div>
      );

      employeeInfoArea = (
        <form>
          <div className="profileText">
            <div className="profileTextItem">
              <div className="profileTextItem-left">
                <p>First name:</p>
              </div>
              <div className="profileTextItem-right">
                <input
                  type="text"
                  defaultValue={firstName}
                  onChange={(value) => {
                    employeeToUpdate.firstName = value;
                  }}
                />
              </div>
            </div>

            <div className="profileTextItem">
              <div className="profileTextItem-left">
                <p>Last name:</p>
              </div>
              <div className="profileTextItem-right">
                <input
                  type="text"
                  defaultValue={lastName}
                  onChange={(value) => {
                    employeeToUpdate.lastName = value;
                  }}
                />
              </div>
            </div>

            <div className="profileTextItem">
              <div className="profileTextItem-left">
                <p>Hired:</p>
              </div>
              <div className="profileTextItem-right">
                <input
                  type="data"
                  defaultValue={date}
                  onChange={(value) => {
                    employeeToUpdate.hired = value;
                  }}
                />
              </div>
            </div>

            <div className="profileTextItem">
              <div className="profileTextItem-left">
                <p>Phone:</p>
              </div>
              <div className="profileTextItem-right">
                <input
                  type="text"
                  defaultValue={phone}
                  onChange={(value) => {
                    employeeToUpdate.phone = value;
                  }}
                />
              </div>
            </div>

            <div className="profileTextItem">
              <div className="profileTextItem-left">
                <p>Email:</p>
              </div>
              <div className="profileTextItem-right">
                <input
                  type="text"
                  defaultValue={email}
                  onChange={(value) => {
                    employeeToUpdate.email = value;
                  }}
                />
              </div>
            </div>

            <div className="profileTextItem">
              <div className="profileTextItem-left">
                <p>Role:</p>
              </div>
              <div className="profileTextItem-right">
                <Select
                  onChange={(value) => {
                    employeeToUpdate.role = value;
                  }}
                  options={roles}
                />
              </div>
            </div>

            <div className="profileTextItem">
              <div className="profileTextItem-left">
                <p>Specialization:</p>
              </div>
              <div className="profileTextItem-right">
                <Select
                  options={specializations}
                  onChange={(value) => {
                    employeeToUpdate.specialization = value;
                  }}
                />
              </div>
            </div>

            <div className="profileTextItem">
              <div className="profileTextItem-left">
                <p>Qualification:</p>
              </div>
              <div className="profileTextItem-right">
                <Select
                  options={qualifications}
                  onChange={(value) => {
                    employeeToUpdate.qualification = value;
                  }}
                />
              </div>
            </div>
          </div>
        </form>
      );
    }

    return (
      <Modal
        styles={{
          modal: {
            background: 'wheat',
            padding: '40px',
            fontFamily: 'Play',
            alignItems: 'center',
            textAlign: 'center',
            fontSize: '45px',
            border: '3px solid black',
            borderRadius: '10px',
          },
        }}
        open={this.props.open}
        center={true}
        showCloseIcon={false}
        onOverlayClick={this.props.setModalState}
      >
        <>
          <div className="profileBody">
            <div className="profilePhoto">
              <img src={testImage} alt="profile" />
            </div>
            {employeeInfoArea}
          </div>
          {actionButtons}
        </>
      </Modal>
    );
  }
}

function mapStateToProps(state) {
  return {
    open: state.open,
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
    isEditing: state.isEditing,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setModalState: () => dispatch(SetModalState()),
    onEdit: () => dispatch(SetEditState()),
    onDelete: (id, menu) => {
      dispatch(DeleteData(id, menu));
    },
    onUpdate: (activeMenu, data) => dispatch(UpdateEmployee(activeMenu, data)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(EmployeeModal);
