import StyleChange from '../../Functions/ChangeStyles';
export const recordsPerPage = (
  <div>
    <p style={{ color: 'wheat' }}>Orders per page </p>
    <button
      className="recordsPerPage"
      id="12" //======================================!!!!!!!!!!!!!!!!!!!!!!
      onClick={(e) => {
        this.props.getOrders(e.target.id, this.props.pageNumber);
        StyleChange(e.target.id, 'recordsPerPage');
      }}
    >
      12
    </button>
    <button
      className="recordsPerPage"
      id="24"
      onClick={(e) => {
        this.props.getOrders(e.target.id, this.props.pageNumber);
        StyleChange(e.target.id, 'recordsPerPage');
      }}
    >
      24
    </button>
    <button
      className="recordsPerPage"
      id="48"
      onClick={(e) => {
        this.props.getOrders(e.target.id, this.props.pageNumber);
        StyleChange(e.target.id, 'recordsPerPage');
      }}
    >
      48
    </button>
  </div>
);
