import React from 'react';
import { connect } from 'react-redux';
import 'react-responsive-modal/styles.css';
import {
  SetModalState,
  SetEditState,
  DeleteData,
} from '../../Redux/Actions/actions.js';
import Select from 'react-select';

class DropdownList extends React.PureComponent {
  render() {
    var list = [];
    this.props.DropdownList.forEach((element) => {
      list.push({ value: element.value, label: element.name });
    });

    return (
      <div className="profileTextItem">
        <div className="profileTextItem-left">
          <p>Qualification:</p>
        </div>
        <div className="profileTextItem-right">
          <Select options={list} />
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    open: state.open,
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
    isEditing: state.isEditing,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setModalState: () => dispatch(SetModalState()),
    onEdit: () => dispatch(SetEditState()),
    onDelete: (id, menu) => {
      dispatch(DeleteData(id, menu));
    },
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(DropdownList);
