import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../../Redux/Actions/actions.js';
import CreateOrderModal from '../ModalWindowsComponents/CreateOrderModal.js';
import UpdateOrderModal from '../ModalWindowsComponents/UpdateOrderModal.js';

class OrdersList extends React.PureComponent {
  render() {
    var data = this.props.pagedTablesData.orders;
    if (data !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>Date of service</th>
            <th>Client name</th>
            <th>Client phone number</th>
            <th>Client email</th>
            <th>Procedure name</th>
            <th>Procedure cost</th>
            <th>Employee</th>
          </tr>
        </thead>
      );
      for (let i = 0; i < data.length; i++) {
        element = '';
        element = (
          <tr
            key={data[i].orderId}
            id={data[i].orderId}
            onClick={async () => {
              this.props.setActiveId(data[i].orderId);
              await this.props.getOrder(data[i].orderId, this.props.activeMenu);
              this.props.setUpdateModalState();
              this.props.getSchedule(
                data[i].employyeId,
                data[i].dateOfService,
                data[i].procedureId,
                this.props.openTime,
                this.props.closeTime,
              );
            }}
          >
            <td>{data[i].dateOfService}</td>
            <td>{data[i].clientFullName}</td>
            <td>{data[i].clientPhoneNumber}</td>
            <td>{data[i].clientEmail}</td>
            <td>{data[i].procedureName}</td>
            <td>{data[i].procedureCost}</td>
            <td>{data[i].employeeFullName}</td>
          </tr>
        );
        elements = elements.concat(element);
      }

      if (data !== '') {
        return (
          <>
            <button
              className="modalButton"
              onClick={() => {
                this.props.setCreateModalState();
              }}
            >
              New order
            </button>
            <div className="dataTable">
              <table className="tableStyle">
                {header}
                <tbody>{elements}</tbody>
                {/* <CreateOrderModal /> */}
                {/* <UpdateOrderModal /> */}
              </table>
            </div>
          </>
        );
      } else {
        return (
          <>
            <p>There is no Data in DB...</p>
          </>
        );
      }
    }
  }
}

function mapStateToProps(state) {
  return {
    pagedTablesData: state.pagedTablesData,
    singleResponseData: state.singleResponseData,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
    openTime: state.openTime,
    closeTime: state.closeTime,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setUpdateModalState: () => dispatch(actions.SetUpdateModalState()),
    setCreateModalState: () => dispatch(actions.SetCreateModalState()),
    setActiveId: (id) => dispatch(actions.SetActiveIdentifier(id)),
    getOrder: (id, activeMenu) =>
      dispatch(actions.FetchActiveRecordData(id, activeMenu)),
    getSchedule: (employeeId, dateOfService, procedureId, open, close) =>
      dispatch(
        actions.GetAvaliableTimes(
          employeeId,
          dateOfService,
          procedureId,
          open,
          close,
        ),
      ),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(OrdersList);
