import {
  CLIENTS_FETC_DATA_SUCCESS,
  EMPLOYEE_AUTORIZATION_SUCCESS,
  ACTIVE_ID_SET,
} from './Actions/actionTypes.js';

const initialState = {
  employeeToken: 'you dont need a token',
  mensHaircut: false,
  modalOpen: true,
  clientsList: '',
  activeId: '',
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case ACTIVE_ID_SET:
      return {
        ...state,
        mensHaircut: true,
      };
    case CLIENTS_FETC_DATA_SUCCESS:
      console.log(action.payload);
      return {
        ...state,
        clientsList: action.payload,
      };

    case EMPLOYEE_AUTORIZATION_SUCCESS:
      console.log(action.payload);
      return {
        ...state,
        employeeToken: action.payload,
        modalOpen: false,
      };

    default:
      return state;
  }
}
