import React from 'react';
import { connect } from 'react-redux';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';
import {
  SetUpdateModalState,
  SetEditState,
  DeleteData,
  UpdateData,
} from '../../Redux/Actions/actions.js';
import Select from 'react-select';

class UpdateOrderModal extends React.PureComponent {
  render() {
    var orderInfoArea;
    var actionButtons;
    var clientName =
      this.props.singleResponseData.client.firstName +
      this.props.singleResponseData.client.lastName;
    var orderToUpdate = {
      dateOfService: this.props.activeDate,
      client: this.props.activeClient,
      procedure: this.props.activeProcedure,
      employee: this.props.activeEmployee,
    };

    var clientList = [];
    this.props.responseDataList.clients.forEach((element) => {
      clientList.push({
        value: element.clientId,
        label: element.firstName + ' ' + element.lastName,
      });
    });

    var procedureTypesList = [];
    this.props.responseDataList.procedureTypes.forEach((element) => {
      procedureTypesList.push({
        value: element.value,
        label: element.text,
      });
    });

    var procedureList = [];
    if (this.props.procedures !== '') {
      this.props.procedures.forEach((element) => {
        procedureList.push({
          value: element.procedureId,
          label: element.procedureName,
        });
      });
    }

    var employeeList = [];
    if (this.props.employees !== '') {
      this.props.employees.forEach((element) => {
        employeeList.push({
          value: element.employeeId,
          label: element.firstName + ' ' + element.lastName,
        });
      });
    }

    var scheduleTimesList = [];
    if (this.props.schedule !== '') {
      this.props.schedule.forEach((time) => {
        scheduleTimesList.push({
          value: time,
          label: time.substr(11),
        });
      });
    }

    if (!this.props.isEditing) {
      actionButtons = (
        <div className="editUserInfo">
          <button className="modalButton" onClick={this.props.onEdit}>
            Edit order
          </button>
          <button
            className="modalButton"
            onClick={() => {
              this.props.onDelete(this.props.activeId, this.props.activeMenu);
              this.props.setUpdateModalState();
            }}
          >
            Delete order
          </button>
        </div>
      );

      orderInfoArea = (
        <div className="profileText">
          <div className="profileTextItem">
            <p>Date of service: {}</p>
          </div>

          <div className="profileTextItem">
            <p>Client name: {}</p>
          </div>

          <div className="profileTextItem">
            <p>Procedure type: {}</p>
          </div>

          <div className="profileTextItem">
            <p>Procedure: {}</p>
          </div>

          <div className="profileTextItem">
            <p>Employee: {}</p>
          </div>

          <div className="profileTextItem">
            <p>Time of service: {} </p>
          </div>
        </div>
      );
    } else {
      actionButtons = (
        <div className="confirmUserInfo">
          <div>
            <input type="file" />
          </div>
          <div>
            <button
              className="modalButton"
              onClick={() => {
                this.props.onEdit();
                this.props.setUpdateModalState();
                this.props.onUpdate(this.props.activeMenu, employeeToUpdate);
              }}
            >
              Save changes
            </button>
          </div>
        </div>
      );

      employeeInfoArea = (
        <div className="profileText">
          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>First name:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="text"
                defaultValue={firstName}
                onChange={(e) => {
                  employeeToUpdate.firstName = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Last name:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="text"
                defaultValue={lastName}
                onChange={(e) => {
                  employeeToUpdate.lastName = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Hired:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="date"
                defaultValue={date}
                onChange={(e) => {
                  employeeToUpdate.hireDate = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Phone:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="tel"
                defaultValue={phone}
                onChange={(e) => {
                  employeeToUpdate.phoneNumber = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Email:</p>
            </div>
            <div className="profileTextItem-right">
              <input
                type="email"
                defaultValue={email}
                onChange={(e) => {
                  employeeToUpdate.email = e.target.value;
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Role:</p>
            </div>
            <div className="profileTextItem-right">
              <Select
                onChange={(data) => {
                  employeeToUpdate.role = data.value;
                }}
                options={roles}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>ProcedureType:</p>
            </div>
            <div className="profileTextItem-right">
              <Select
                options={procedureTypesSL}
                onChange={(data) => {
                  this.props.responseDataList.procedureTypes.forEach(
                    (element) => {
                      if (element.procedureTypeName === data.label) {
                        employeeToUpdate.procedureType = element;
                      }
                    },
                  );
                }}
              />
            </div>
          </div>

          <div className="profileTextItem">
            <div className="profileTextItem-left">
              <p>Qualification:</p>
            </div>
            <div className="profileTextItem-right">
              <Select
                options={qualifications}
                onChange={(data) => {
                  employeeToUpdate.qualification = data.value;
                }}
              />
            </div>
          </div>
        </div>
      );
    }

    return (
      <Modal
        styles={{
          modal: {
            maxWidth: 'fit-content',
            background: '#6c3636',
            padding: '40px',
            fontFamily: 'Play',
            alignItems: 'center',
            textAlign: 'center',
            border: '6px solid #f7f6f6',
            borderRadius: '10px',
          },
        }}
        open={this.props.updateModalOpen}
        center={true}
        showCloseIcon={false}
        onOverlayClick={this.props.setUpdateModalState}
      >
        <>
          <div className="profileBody">
            <div className="profilePhoto">
              <img src={testImage} alt="profile" />
            </div>
            {employeeInfoArea}
          </div>
          {actionButtons}
        </>
      </Modal>
    );
  }
}

function mapStateToProps(state) {
  return {
    updateModalOpen: state.updateModalOpen,
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
    isEditing: state.isEditing,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setUpdateModalState: () => dispatch(SetUpdateModalState()),
    onEdit: () => dispatch(SetEditState()),
    onDelete: (id, menu) => {
      dispatch(DeleteData(id, menu));
    },
    onUpdate: (activeMenu, data) => dispatch(UpdateData(activeMenu, data)),
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(UpdateEmployeeModal);
