import React from 'react';
import { connect } from 'react-redux';
import {
  MaterialsFeetchData,
  SetActiveIdentifier,
} from '../../Redux/Actions/actions.js';

function styleChange(id) {
  var active = document.querySelector('.active');
  console.log('active', active);
  if (active !== null) {
    active.classList.toggle('active');
  }
  var row = document.getElementById(`${id}`);
  row.classList.toggle('active');
}

class MaterialsList extends React.PureComponent {
  render() {
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>Name</th>
            <th>Amount</th>
            <th>Manufacturer</th>
          </tr>
        </thead>
      );

      for (let i = 0; i < this.props.responseDataList.length; i++) {
        element = '';
        element = (
          <tr
            key={this.props.responseDataList[i].materialId}
            id={this.props.responseDataList[i].materialId}
            onClick={() => {
              this.props.setActiveId(this.props.responseDataList[i].materialId);
              styleChange(this.props.responseDataList[i].materialId);
            }}
          >
            <td>{this.props.responseDataList[i].materialName}</td>
            <td>{this.props.responseDataList[i].materialAmount}</td>
            <td>
              {
                this.props.responseDataList[i].materialManufacturer
                  .manufacturerName
              }
            </td>
          </tr>
        );
        elements = elements.concat(element);
      }
      console.log('elements', elements);
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
            </table>
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    activeId: state.activeId,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
    getMaterials: () => dispatch(MaterialsFeetchData()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(MaterialsList);
