import React from 'react';
import { EmployeeAutorization } from '../Redux/Actions/actions.js';
import { connect } from 'react-redux';

class Autorization extends React.PureComponent {
  render() {
    return (
      <div>
        <p>Email: </p>
        <input type="text" className="employeeCredentials" id="employeeEmail" />
        <p>Password: </p>
        <input
          type="password"
          className="employeeCredentials"
          id="employeePassword"
        />
        <div>
          <button
            className="modalButton"
            onClick={() => {
              this.props.onAuth(
                document.getElementById('employeeEmail').value,
                document.getElementById('employeePassword').value,
              );
            }}
          >
            Login
          </button>
        </div>
      </div>
    );
  }
}

function mapDispatchToProps(dispatch) {
  return {
    onAuth: (email, password) =>
      dispatch(EmployeeAutorization(email, password)),
  };
}

export default connect(null, mapDispatchToProps)(Autorization);
