import React from 'react';
import { connect } from 'react-redux';
import {
  SetActiveIdentifier,
  SetUpdateModalState,
  EmployeeFetchData,
  SetCreateModalState,
} from '../../Redux/Actions/actions.js';
import CreateEmployeeModal from '../ModalWindowsComponents/CreateEmployeeModal.js';
import UpdateEmployeeModal from '../ModalWindowsComponents/UpdateEmployeeModal.js';

class EmployeeList extends React.PureComponent {
  render() {
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>FirstName</th>
            <th>Lastname</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Role</th>
            <th>Specialization</th>
            <th>Qualification</th>
          </tr>
        </thead>
      );

      for (let i = 0; i < this.props.responseDataList.employees.length; i++) {
        var role;
        var specialization;
        var qualification;

        // eslint-disable-next-line no-loop-func
        this.props.responseDataList.specialization.forEach((element) => {
          if (
            element.value ===
            this.props.responseDataList.employees[i].specialization
          ) {
            specialization = element.name;
          }
        });

        // eslint-disable-next-line no-loop-func
        this.props.responseDataList.roles.forEach((element) => {
          if (element.value === this.props.responseDataList.employees[i].role) {
            role = element.name;
          }
        });

        // eslint-disable-next-line no-loop-func
        this.props.responseDataList.qualification.forEach((element) => {
          if (
            element.value ===
            this.props.responseDataList.employees[i].qualification
          ) {
            qualification = element.name;
          }
        });

        element = '';
        element = (
          <tr
            key={this.props.responseDataList.employees[i].employeeId}
            id={this.props.responseDataList.employees[i].employeeId}
            onClick={() => {
              this.props.setActiveId(
                this.props.responseDataList.employees[i].employeeId,
              );
              this.props.getEmployee(
                this.props.responseDataList.employees[i].employeeId,
              );
              this.props.setUpdateModalState();
            }}
          >
            <td>{this.props.responseDataList.employees[i].firstName}</td>
            <td>{this.props.responseDataList.employees[i].lastName}</td>
            <td>{this.props.responseDataList.employees[i].phoneNumber}</td>
            <td>{this.props.responseDataList.employees[i].email}</td>
            <td>{role}</td>
            <td>{specialization}</td>
            <td>{qualification}</td>
          </tr>
        );
        elements = elements.concat(element);
      }
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <button
            className="modalButton"
            onClick={() => {
              this.props.setCreateModalState();
            }}
          >
            Create employee
          </button>
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
              <CreateEmployeeModal />
              <UpdateEmployeeModal />
            </table>
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    activeId: state.activeId,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setUpdateModalState: () => dispatch(SetUpdateModalState()),
    setCreateModalState: () => dispatch(SetCreateModalState()),
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
    getEmployee: (id) => dispatch(EmployeeFetchData(id)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(EmployeeList);
