import React from 'react';
import { connect } from 'react-redux';
import {
  MediaFilesFeetchData,
  SetActiveIdentifier,
} from '../../Redux/Actions/actions.js';

function styleChange(id) {
  var active = document.querySelector('.active');
  console.log('active', active);
  if (active !== null) {
    active.classList.toggle('active');
  }
  var row = document.getElementById(`${id}`);
  row.classList.toggle('active');
}

class MediaFilesList extends React.PureComponent {
  render() {
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>Mediafile</th>
            {/* <th>Amount</th>
            <th>Manufacturer</th> */}
          </tr>
        </thead>
      );

      let arr;
      for (let i = 0; i < this.props.responseDataList.length; i++) {
        arr = arr.concat(this.props.responseDataList[i]);
      }

      console.log('initial ', this.props.responseDataList);
      console.log('array ', arr);

      for (let i = 0; i < this.props.responseDataList.length; i++) {
        element = '';
        element = (
          <tr className="dataTable">
            <td>{this.props.responseDataList[i].contentType}</td>
          </tr>
        );
        elements = elements.concat(element);
      }
      console.log('elements', elements);
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
            </table>
            <div className="tableButton">
              <button onClick={this.props.getMedia}>MediaFilesList</button>
              <button>Add client</button>
              <button>More info</button>
            </div>
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    activeId: state.activeId,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
    getMedia: () => dispatch(MediaFilesFeetchData()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(MediaFilesList);
