import React from 'react';
import { connect } from 'react-redux';
import { GetImage, SetActiveIdentifier } from '../../Redux/Actions/actions.js';

class MediaFilesList extends React.PureComponent {
  render() {
    var data = this.props.pagedTablesData;
    if (data !== '') {
      var elements = [];
      var element;

      var arr = [];
      for (let i = 0; i < data.length; i++) {
        arr = arr.concat(data[i]);
      }

      if (data.length > 0) {
        for (let i = 0; i < data.length; i++) {
          element = [];
          element = (
            <dir
              className="profileCardBody"
              key={data[i].mediaFileId}
              id={data[i].mediaFileId}
              onClick={() => {
                this.props.setActiveId(data[i].employeeId);
                this.props.getEmployee(
                  data[i].employeeId,
                  this.props.activeMenu,
                );
                this.props.setUpdateModalState();
                this.props.getProfilePhotoByEmployeeId(data[i].employeeId);
              }}
            >
              <div className="profileCardInfo">
                <p>{data[i].fullName}</p>
                <div className="profilePhoto">
                  <img
                    src={`data:image/gif;base64,${data[i].fileData}`}
                    alt=""
                  />
                </div>
              </div>
            </dir>
          );
          elements = elements.concat(element);
        }
      }
    }

    if (data !== '') {
      return (
        <>
          <div className="dataTable">
            <table className="tableStyle">{elements}</table>
          </div>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    pagedTablesData: state.pagedTablesData,
    singleResponseData: state.singleResponseData,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getImage: (id) => dispatch(GetImage(id)),
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(MediaFilesList);
