import React from 'react';
import { connect } from 'react-redux';
import { SetActiveIdentifier } from '../../Redux/Actions/actions.js';
import { CreateServiceModal } from '../ModalWindowsComponents/CreateServiceModal.js';
import { UpdateServiceModal } from '../ModalWindowsComponents/UpdateServiceModal.js';

class ServicesList extends React.PureComponent {
  render() {
    if (this.props.responseDataList.procedures !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>Name</th>
            <th>Type</th>
            <th>Time amount</th>
            <th>Price</th>
          </tr>
        </thead>
      );

      for (let i = 0; i < this.props.responseDataList.procedures.length; i++) {
        element = '';
        element = (
          <tr
            key={this.props.responseDataList.procedures[i].procedureId}
            id={this.props.responseDataList.procedures[i].procedureId}
            onClick={() => {
              this.props.setActiveId(
                this.props.responseDataList.procedures[i].procedureId,
              );
            }}
          >
            <td>{this.props.responseDataList.procedures[i].procedureName}</td>
            <td>
              {
                this.props.responseDataList.procedures[i].procedureType
                  .procedureTypeName
              }
            </td>
            <td>{this.props.responseDataList.procedures[i].timeAmount}</td>
            <td>{this.props.responseDataList.procedures[i].procedurePrice}</td>
          </tr>
        );
        elements = elements.concat(element);
      }
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <button
            className="modalButton"
            onClick={() => {
              this.props.setCreateModalState();
            }}
          >
            New service
          </button>
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
            </table>
            <CreateServiceModal />
            {/* <UpdateServiceModal /> */}
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    activeId: state.activeId,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(ServicesList);
