import React from 'react';
import { EmployeeAutorization } from '../Redux/Actions/actions.js';
import { connect } from 'react-redux';

class Autorization extends React.PureComponent {
  render() {
    return (
      <div>
        <form>
          <p>Email: </p>
          <input type="text" id="employeeEmail" />
          <p>Password: </p>
          <input type="text" id="employeePassword" />
          <button
            type="submit"
            onSubmit={() => {
              this.props.onAuth(
                document.getElementById('employeeEmail').value,
                document.getElementById('employeePassword').value,
              );
            }}
          >
            Login
          </button>
        </form>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    token: state.token,
  };
}
function mapDispatchToProps(dispatch) {
  return {
    onAuth: (email, password) =>
      dispatch(EmployeeAutorization(email, password)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Autorization);
