import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../../Redux/Actions/actions.js';
import Carousel from 'react-elastic-carousel';

class PromotionsList extends React.PureComponent {
  componentDidMount = () => {
    this.props.getAllMedia();
  };
  render() {
    var data = this.props.pagedTablesData.employees;

    if (data !== '') {
      var elements = [];
      var element;
      var promoImg = '';
      for (let i = 0; i < data.length; i++) {
        if (data[i].mediaFiles.length > 0) {
          // eslint-disable-next-line no-loop-func
          data[i].mediaFiles.forEach((file) => {
            if (file.isProfilePhoto)
              promoImg = `data:image/gif;base64,${file.fileData}`;
          });
        }
        element = [];
        element = (
          <dir
            className="profileCardBody"
            key={data[i].employeeId}
            id={data[i].employeeId}
            onClick={() => {
              if (this.props.isEditing) {
                this.props.onEdit();
              }
              this.props.setActiveId(data[i].employeeId);
              this.props.getEmployee(data[i].employeeId, 'staffPage');
              this.props.setUpdateModalState();
              this.props.getProfilePhotoByEmployeeId(data[i].employeeId);
            }}
          >
            <div className="profileCardInfo">
              <p>{data[i].fullName}</p>
              <div className="profilePhoto">
                <img src={promoImg} alt="" />
              </div>
            </div>
          </dir>
        );
        elements.push(element);
      }
    }

    if (data !== '') {
      return (
        <>
          <p className="title">Promotions</p>
          <div className="employeePage">
            <Carousel itemsToShow={3}>{elements}</Carousel>
          </div>
        </>
      );
    } else {
      return <p>There is no promo today...</p>;
    }
  }
}

function mapStateToProps(state) {
  return {
    pagedTablesData: state.pagedTablesData,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getAllMedia: (activeMenu, type, id) =>
      dispatch(actions.FeetchAllMediafiles(activeMenu, type, id)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(PromotionsList);
