import {
  click,
  CLIENTS_FETC_DATA_SUCCESS,
  EMPLOYEE_AUTHENTICATION_SUCCESS,
} from './Actions/actionTypes.js';

const initialState = {
  token: null,
  role: '',
  mensHaircut: false,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case click:
      return {
        ...state,
        mensHaircut: true,
      };
    case CLIENTS_FETC_DATA_SUCCESS:
      return action.payload;

    case EMPLOYEE_AUTHENTICATION_SUCCESS:
      return action.payload;

    default:
      return state;
  }
}
