import React from 'react';
import { connect } from 'react-redux';
import {
  ServicesFeetchData,
  SetActiveIdentifier,
} from '../../Redux/Actions/actions.js';

function styleChange(id) {
  var active = document.querySelector('.active');
  console.log('active', active);
  if (active !== null) {
    active.classList.toggle('active');
  }
  var row = document.getElementById(`${id}`);
  row.classList.toggle('active');
}

class ServicesList extends React.PureComponent {
  render() {
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>Name</th>
            <th>Type</th>
            <th>Time amount</th>
            <th>Price</th>
          </tr>
        </thead>
      );

      for (let i = 0; i < this.props.responseDataList.length; i++) {
        element = '';
        element = (
          <tr
            key={this.props.responseDataList[i].procedureId}
            id={this.props.responseDataList[i].procedureId}
            onClick={() => {
              this.props.setActiveId(
                this.props.responseDataList[i].procedureId,
              );
              styleChange(this.props.responseDataList[i].procedureId);
            }}
          >
            <td>{this.props.responseDataList[i].procedureName}</td>
            <td>{this.props.responseDataList[i].procedureType}</td>
            <td>{this.props.responseDataList[i].timeAmount}</td>
            <td>{this.props.responseDataList[i].procedurePrice}</td>
          </tr>
        );
        elements = elements.concat(element);
      }
      console.log('elements', elements);
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
            </table>
            <div className="tableButton">
              <button onClick={this.props.getServices}>ServicesList</button>
              <button>Add client</button>
              <button>More info</button>
            </div>
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    activeId: state.activeId,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
    getServices: () => dispatch(ServicesFeetchData()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(ServicesList);
