import {
    click,
    onRBChange,
    onUIChange,
    addLimit,
} from './Actions/actionTypes.js';

const initialState = {
    firstRender: true,
    mensHaircut: false
};

export default function reducer(state = initialState, action) {
    switch (action.type) {
        case click:
            return {
                ...state,
                firstRender: false,
                mensHaircut: true
            };

        default:
            return state;
    }
}
