import React from 'react';
import { connect } from 'react-redux';
import {
  FeedbacksFeetchData,
  SetActiveIdentifier,
} from '../../Redux/Actions/actions.js';

function styleChange(id) {
  var active = document.querySelector('.active');
  console.log('active', active);
  if (active !== null) {
    active.classList.toggle('active');
  }
  var row = document.getElementById(`${id}`);
  row.classList.toggle('active');
}

class FeedbacksList extends React.PureComponent {
  render() {
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      var header = (
        <thead>
          <tr>
            <th>Title</th>
            <th>Text</th>
          </tr>
        </thead>
      );

      for (let i = 0; i < this.props.responseDataList.length; i++) {
        element = '';
        element = (
          <tr
            className="dataTable"
            key={this.props.responseDataList[i].feedbackId}
            id={this.props.responseDataList[i].feedbackId}
            onClick={() => {
              this.props.setActiveId(this.props.responseDataList[i].feedbackId);
              styleChange(this.props.responseDataList[i].feedbackId);
            }}
          >
            <td>{this.props.responseDataList[i].feedbackTitle}</td>
            <td>{this.props.responseDataList[i].feedbackText}</td>
          </tr>
        );
        elements = elements.concat(element);
      }
      console.log('elements', elements);
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
            </table>
            <div className="tableButton">
              <button onClick={this.props.getFeedbacks}>FeedbacksList</button>
              <button>Approve</button>
              <button>More info</button>
            </div>
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    activeId: state.activeId,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
    getFeedbacks: () => dispatch(FeedbacksFeetchData()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(FeedbacksList);
