import * as TYPES from './actionTypes';

// Authorization of employees -----------------------------------------------------------------------------

export function EmployeeAutorization(email, password) {
  const requestURL = `https://localhost:7275/Employee/GetToken?employeeEmail=${email}&password=${password}`;
  return (dispatch) => {
    fetch(requestURL, {
      method: 'post',
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response;
      })
      .then(async (response) => {
        const data = await response.json();
        dispatch(EmployeeAutorizationSuccess(data));
      });
  };
}

export function EmployeeAutorizationSuccess(data) {
  return {
    type: TYPES.EMPLOYEE_AUTORIZATION_SUCCESS,
    payload: data,
  };
}

export function EmployeeLogout() {
  return {
    type: TYPES.EMPLOYEE_LOGOUT,
  };
}

//Active identifier setting------------------------------

export function SetActiveIdentifier(id) {
  return {
    type: TYPES.ACTIVE_ID_SET,
    payload: id,
  };
}

export function FeetchData(id) {
  var identifier = id;
  var requestURL;
  switch (id) {
    case 'clientsPage':
      requestURL = 'https://localhost:7275/Client/GetAllClients';
      break;
    case 'materialsPage':
      requestURL = 'https://localhost:7275/Material/GetAllMaterials';
      break;
    case 'servicesPage':
      requestURL = 'https://localhost:7275/Procedure/GetAllProcedures';
      break;
    case 'ordersPage':
      requestURL = 'https://localhost:7275/Order/GetAllOrders';
      break;
    case 'schedulesPage':
      requestURL = 'https://localhost:7275/Schedule/GetAllSchedules';
      break;
    case 'profilesPage':
      requestURL = 'https://localhost:7275/Profile/GetAllProfiles';
      break;
    case 'promoPage':
      requestURL = 'https://localhost:7275/Profile/GetAllProfiles';
      break;
    case 'feedbacksPage':
      requestURL = 'https://localhost:7275/Feedback/GetAllFeedbacks';
      break;
    case 'stuffPage':
      requestURL = 'https://localhost:7275/Employee/GetAllEmployees';
      break;
    default:
      break;
  }
  return (dispatch) => {
    fetch(requestURL)
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response;
      })
      .then(async (response) => {
        const data = await response.json();
        dispatch(FeetchDataSuccess(data, identifier));
      });
  };
}

export function FeetchDataSuccess(data, id) {
  return {
    type: TYPES.FETCH_DATA_SUCCESS,
    payload: data,
    identifier: id,
  };
}

//Get by id----------------------------------------------------

export function EmployeeFetchData(id) {
  const requestURL = `https://localhost:7275/Employee/GetEmployeeById?id=${id}`;
  return (dispatch) => {
    fetch(requestURL)
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response;
      })
      .then(async (response) => {
        const data = await response.json();
        dispatch(EmployeeFeetchDataSuccess(data));
      });
  };
}

export function EmployeeFeetchDataSuccess(data) {
  return {
    type: TYPES.SINGLE_FETCH_DATA_SUCCESS,
    payload: data,
  };
}

//--------------------------------------------------------

// States processing functions

export function SetEditState() {
  return {
    type: TYPES.EDIT_STATE,
  };
}

export function SetUpdateModalState() {
  alert('KUUUUUUUUUUUURWWWWWAAAA!!!');
  return {
    type: TYPES.UPDATE_MODAL_WINDOW_STATE,
  };
}

export function SetCreateModalState() {
  return {
    type: TYPES.CREATE_MODAL_WINDOW_STATE,
  };
}

//Delete record

export function DeleteData(id, activeMenu) {
  var requestURL;
  switch (activeMenu) {
    case 'clientsPage':
      requestURL = `https://localhost:7275/Client/DeleteClientById?id=${id}`;
      break;
    case 'materialsPage':
      requestURL = `https://localhost:7275/Material/DeleteMaterialById?id=${id}`;
      break;
    case 'servicesPage':
      requestURL = `https://localhost:7275/Procedure/DeleteServiceById?id=${id}`;
      break;
    case 'ordersPage':
      requestURL = `https://localhost:7275/Order/DeleteOrderById?id=${id}`;
      break;
    case 'schedulesPage':
      requestURL = `https://localhost:7275/Schedule/DeleteScheduletById?id=${id}`;
      break;
    case 'profilesPage':
      requestURL = `https://localhost:7275/Profile/DeleteProfileById?id=${id}`;
      break;
    case 'promoPage':
      requestURL = `https://localhost:7275/Profile/DeleteProfileById?id=${id}`;
      break;
    case 'feedbacksPage':
      requestURL = `https://localhost:7275/Feedback/DeleteFeedbackById?id=${id}`;
      break;
    case 'stuffPage':
      requestURL = `https://localhost:7275/Employee/DeleteEmployeeById?id=${id}`;
      break;
    default:
      break;
  }
  return (dispatch) => {
    fetch(requestURL, { method: 'post', cors: 'no-cors' }).then(() =>
      dispatch(FeetchData(activeMenu)),
    );
  };
}

export function UpdateData(activeMenu, dataObject) {
  var requestURL;
  switch (activeMenu) {
    case 'clientsPage':
      requestURL = 'https://localhost:7275/Client/UpdateClient';
      break;
    case 'materialsPage':
      requestURL = 'https://localhost:7275/Material/UpdateMaterial';
      break;
    case 'servicesPage':
      requestURL = 'https://localhost:7275/Procedure/UpdateProcedure';
      break;
    case 'ordersPage':
      requestURL = 'https://localhost:7275/Order/UpdateOrder';
      break;
    case 'schedulesPage':
      requestURL = 'https://localhost:7275/Schedule/UpdateSchedule';
      break;
    case 'profilesPage':
      requestURL = 'https://localhost:7275/Profile/UpdateProfile';
      break;
    case 'promoPage':
      requestURL = 'https://localhost:7275/Profile/UpdateProfile';
      break;
    case 'feedbacksPage':
      requestURL = 'https://localhost:7275/Feedback/UpdateFeedback';
      break;
    case 'stuffPage':
      requestURL = 'https://localhost:7275/Employee/UpdateEmployee';
      break;
    default:
      break;
  }
  return (dispatch) => {
    fetch(requestURL, {
      method: 'POST',
      cors: 'no-cors',
      headers: new Headers({
        Accept: 'application/json',
        'content-type': 'application/json',
      }),
      body: JSON.stringify(dataObject),
    })
      .then((response) => {
        if (response.status !== 200) {
          console.log('not ok' + response.status);
        }
      })
      .catch((err) => {
        console.log(err);
      })
      .then(() => dispatch(FeetchData(activeMenu)));
  };
}

//-------------------- ADD DATA

export function AddData(activeMenu, dataObject) {
  var requestURL;
  switch (activeMenu) {
    case 'clientsPage':
      requestURL = 'https://localhost:7275/Client/CreateClient';
      break;
    case 'materialsPage':
      requestURL = 'https://localhost:7275/Material/CreateMaterial';
      break;
    case 'servicesPage':
      requestURL = 'https://localhost:7275/Procedure/CreateProcedure';
      break;
    case 'ordersPage':
      requestURL = 'https://localhost:7275/Order/CreateOrder';
      break;
    case 'schedulesPage':
      requestURL = 'https://localhost:7275/Schedule/CreateSchedule';
      break;
    case 'profilesPage':
      requestURL = 'https://localhost:7275/Profile/CreateProfile';
      break;
    case 'promoPage':
      requestURL = 'https://localhost:7275/Profile/CreateProfile';
      break;
    case 'feedbacksPage':
      requestURL = 'https://localhost:7275/Feedback/CreateFeedback';
      break;
    case 'stuffPage':
      requestURL = 'https://localhost:7275/Employee/CreateEmployee';
